import React, { useState } from "react";
import "./styles/biggestWinsLosses.css";

const BiggestWinsLosses = ({ topProfitableTrades }) => {
  const [showPositive, setShowPositive] = useState(true);

  const toggleProfitDisplay = () => {
    setShowPositive(!showPositive);
  };

  return (
    <div className="wins-lost">
      <div className="card">
        <p>P/L</p>
        <p className="menu" onClick={toggleProfitDisplay}>
          ::
        </p>{" "}
        {/* Toggle display on click */}
      </div>
      <div className="card-list">
        <ul className="cards">
          {topProfitableTrades.map((trade, index) => {
            if (
              (trade.profit >= 0 && showPositive) ||
              (trade.profit < 0 && !showPositive)
            ) {
              // Determine whether to display trade based on showPositive state
              return (
                <li className="card-item" key={index}>
                  <div className="card-content">
                    <p>{trade.option_symbol}</p>
                    <p>${(trade.profit * 100).toFixed(2)}</p>
                  </div>
                </li>
              );
            } else {
              return null; // Hide trade if it doesn't match current display criteria
            }
          })}
        </ul>
      </div>
    </div>
  );
};

export default BiggestWinsLosses;

