import React, { useEffect, useRef, useState } from "react";
import { fetchTrades } from "./ApiCalls";

const Graph = () => {
  // Reference to the chart canvas element
  const chartRef = useRef(null);

  // State variables for loading status, trade data, and profits
  const [loading, setLoading] = useState(true);
  const [trades, setTrades] = useState([]);
  const [profits, setProfits] = useState({});

  // Fetch trade data from backend
  useEffect(() => {
    console.log("Fetching trades...");
    fetchTrades()
      .then((data) => {
        console.log("Fetched trades:", data);
        setTrades(data);
        const profitMap = {};
        data.forEach((trade) => {
          console.log("Processing trade:", trade);
          const profitLoss = trade.profitLoss;
          const date = trade.closeDate;
          if (!profitMap[date]) {
            profitMap[date] = 0;
          }
          profitMap[date] += profitLoss;
        });
        console.log("Profit map:", profitMap);
        setProfits(profitMap);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching trades:", error);
      });
  }, []);

  // Categorize each trade under: "1D", "1W", "1M", "3M", "YTD", "1Y", "Max" based on close date
  const categorizeTrades = () => {
    console.log("Categorizing trades...");
    const categorizedTrades = {};
    Object.keys(profits).forEach((date) => {
      const category = getTradeCategory(date);
      console.log("Processing date:", date, "Category:", category);
      if (!categorizedTrades[category]) {
        categorizedTrades[category] = 0;
      }
      categorizedTrades[category] += profits[date];
    });
    console.log("Categorized trades:", categorizedTrades);
    return categorizedTrades;
  };

  const getTradeCategory = (date) => {
    const now = new Date();
    const oneDay = 1000 * 60 * 60 * 24;
    const oneWeek = oneDay * 7;
    const oneMonth = oneDay * 30;
    const threeMonths = oneDay * 90;
    const oneYear = oneDay * 365;

    console.log("Getting trade category for date:", date);
    if (date > now - oneDay) {
      return "1D";
    } else if (date > now - oneWeek) {
      return "1W";
    } else if (date > now - oneMonth) {
      return "1M";
    } else if (date > now - threeMonths) {
      return "3M";
    } else if (date > now - oneYear) {
      return "1Y";
    } else {
      return "Max";
    }
  };

  // Calculate total profit/loss for each category
  const calculateProfitLoss = () => {
    console.log("Calculating profit loss...");
    const categorizedProfits = categorizeTrades();
    return Object.keys(categorizedProfits).reduce((acc, category) => {
      acc[category] = categorizedProfits[category];
      return acc;
    }, {});
  };

  // Effect hook to render chart when trade data and profits are available
  useEffect(() => {
    console.log("Rendering chart...");
    if (!loading && trades.length > 0) {
      console.log("Rendering chart with trades:", trades);
      const ctx = chartRef.current.getContext("2d");
      console.log("Got chart context:", ctx);
      const categories = ["1D", "1W", "1M", "3M", "YTD", "1Y", "Max"];
      console.log("Creating chart with categories:", categories);
      const profitValues = Object.values(calculateProfitLoss());
      console.log("Calculating profit values:", profitValues);
      const backgroundColors = profitValues.map((value) =>
        value >= 0 ? "#bed1be" : "rgb(255, 204, 204)"
      );

      console.log("Creating chart with backgroundColors:", backgroundColors);

      const myChart = new window.Chart(ctx, {
        type: "bar",
        data: {
          //   labels: Object.keys(calculateProfitLoss()),
          labels: categories,
          datasets: [
            {
              label: "Profit/Loss",
              data: profitValues,
              backgroundColor: backgroundColors,
              barThickness: 40,
            },
          ],
        },
        options: {
          plugins: {
            legend: {
              display: false,
            },
          },
          scales: {
            x: {
              grid: {
                display: false,
              },
            },
            y: {
              display: true,
              title: {
                display: true,
                text: "Profit/Loss",
              },
              grid: {
                color: (context) =>
                  context.tick.value === 0
                    ? "rgba(0, 0, 0, 1)"
                    : "rgba(0, 0, 0, 0)",
                lineWidth: 0.5,
              },
              ticks: {
                callback: function (value, index, values) {
                  return value === 0 ? value : "";
                },
              },
            },
          },
        },
      });

      console.log("Created chart instance:", myChart);

      // Cleanup function to destroy chart instance
      return () => {
        console.log("Destroying chart instance...");
        if (myChart) {
          myChart.destroy();
        }
      };
    }
  }, [loading, trades]);

  // Render loading message or chart canvas
  return (
    <div className="graph">
      {loading ? <p>Loading...</p> : <canvas ref={chartRef} />}
    </div>
  );
};

export default Graph;
