import React, { useState, useEffect } from "react";

const Trade = () => {
  // Define state variables
  const [trades, setTrades] = useState([]);
  const [error, setError] = useState(null);

  // Function to fetch the list of trades from the backend
  const fetchTrades = async () => {
    try {
      // Make API call to fetch trades data
      const response = await fetch("http://localhost:8000/trades");
      if (!response.ok) {
        throw new Error("Failed to fetch trades data");
      }
      const data = await response.json();
      // Update state with fetched trades data
      setTrades(data);
    } catch (error) {
      // Handle errors
      setError(error.message);
    }
  };

  // UseEffect hook to fetch trades data when component mounts
  useEffect(() => {
    fetchTrades();
  }, []);

  // Render the component JSX
  return (
    <div>
      {error && <p>Error: {error}</p>}
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Options</th>
            <th>Trade Date</th>
            <th>Settle Date</th>
            <th>Price</th>
            <th>QTY</th>
            <th>Principle</th>
            <th>Exit</th>
            <th>%</th>
            <th>Net Amount</th>
            <th>P/L</th>
          </tr>
        </thead>
        <tbody>
          {trades.map((trade) => (
            <tr key={trade.trade_id}>
              <td>{trade.trade_id}</td>
              <td>
                {trade.symbol} {formatDate(trade.exp_date)} {trade.option_type}{" "}
                ${trade.strike_price}
              </td>
              <td>{formatDate(trade.entry_date)}</td>
              <td>{formatDate(trade.close_date)}</td>
              <td>${trade.entry_price}</td>
              <td>{trade.contracts}</td>
              <td>${(trade.entry_price * trade.contracts).toFixed(2)}</td>
              <td>${trade.exit_price ? trade.exit_price : ""}</td>
              <td>
                {trade.exit_price && trade.entry_price
                  ? (
                      ((trade.exit_price - trade.entry_price) /
                        trade.entry_price) *
                      100
                    ).toFixed(2) + "%"
                  : ""}
              </td>
              <td>
                ${(trade.exit_price ? trade.exit_price : 0) * trade.contracts}
              </td>
              <td>${(trade.profit_loss * trade.contracts).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Trade;
