import React, { useState, useEffect } from "react";
// import "./styles/home.css";
import Account from "./components/Account";
import NewTrade from "./components/NewTrade";
import RecentTrades from "./components/RecentTrades";
import BiggestWinsLosses from "./components/BiggestWinsLosses";
import {
  formatDate,
  showSuccessModal,
  showUnsuccessModal,
} from "./components/Utility";

const Home = () => {
  const [recentTrades, setRecentTrades] = useState([]);

  useEffect(() => {
    fetchRecentTrades(); //get trades from backend
  }, []);

  const fetchRecentTrades = () => {
    fetch("http://localhost:8000/trades/")
      .then((response) => response.json())
      .then((data) => {
        setRecentTrades(data); // Update state with fetched data
      })
      .catch((error) => {
        console.error("Error fetching recent trades:", error);
      });
  };

  // submit new trade
  const handleNewTradeSubmit = (formData) => {
    // POST request to backend to add new trade
    fetch("http://localhost:8000/trades/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (response.ok) {
          fetchRecentTrades();
          showSuccessModal();
        } else {
          throw new Error(response.statusText); // Throw an error with the response status text
        }
      })
      .catch((error) => {
        console.error("Error adding new trade:", error);
        showUnsuccessModal(error.message); // Show the error modal with the error message
      });
  };

  // Function to refresh the list of recent trades
  const handleRefresh = () => {
    fetchRecentTrades();
    showSuccessModal();
  };

  return (
    <div>
      <div className="grid-container">
        <Account trades={recentTrades} />
        <RecentTrades
          recentTrades={recentTrades}
          formatDate={formatDate}
          onRefresh={handleRefresh}
        />
        <BiggestWinsLosses recentTrades={recentTrades} />
      </div>
    </div>
  );
};

export default Home;
