import React, { useState, useEffect } from "react";
import "./styles/initialCash.css";

const InitialCash = ({ onInitialCashSubmit }) => {
  const [initialCash, setInitialCash] = useState("");
  const [displayedInitialCash, setDisplayedInitialCash] = useState(null);
  const [submitted, setSubmitted] = useState(false); // Track if form is submitted

  useEffect(() => {
    console.log("Displayed initial cash:", displayedInitialCash);
  }, [displayedInitialCash]); // Run this effect whenever displayedInitialCash changes

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Form submitted!");

    try {
      if (!isNaN(parseFloat(initialCash))) {
        console.log("Initial cash:", initialCash);
        await onInitialCashSubmit(parseFloat(initialCash));
        console.log("Initial cash submitted successfully!");
        setDisplayedInitialCash(parseFloat(initialCash));
        setSubmitted(true); // Mark form as submitted
      }
    } catch (error) {
      console.error("Error submitting initial cash:", error.message);
      // Handle error if needed
    }
  };

  return (
    <div className="initial-cash-container">
      {displayedInitialCash !== null && (
        <p className="initial-cash-display">
          Initial Cash: ${displayedInitialCash.toFixed(2)}
        </p>
      )}
      <form className="initial" onSubmit={handleSubmit}>
        <label htmlFor="initial-cash">Initial Cash:</label>
        <input
          id="initial-cash"
          type="number"
          placeholder="Enter initial cash amount"
          value={initialCash}
          onChange={(e) => setInitialCash(e.target.value)}
          required
          className={submitted ? "submitted" : ""} // Apply class if form is submitted
        />

        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default InitialCash;
