// Cash.js

import React, { useState, useEffect } from "react";
import "./styles/styles.css";

const Cash = () => {
  const [cashEntries, setCashEntries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchCash = async () => {
    try {
      const response = await fetch("http://localhost:8000/cash");
      if (!response.ok) {
        throw new Error("Failed to fetch cash data");
      }
      const data = await response.json();
      setCashEntries(data.cash_entries);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCash();
  }, []);

  if (loading) {
    return <p>Loading cash information...</p>;
  }

  if (error) {
    return <p>Error fetching cash data: {error}</p>;
  }

  return (
    <div className="cash">
      <div className="header">Cash</div>
      {cashEntries.map((entry, index) => (
        <div className="metric" key={index}>
          <p className="text">${entry[0]}.00</p>
        </div>
      ))}
    </div>
  );
};

export default Cash;
