import React, { useState } from "react";
import { formatDate } from "../func/functions";

const UpdateWatchlist = ({ watchlist, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    target_hit: watchlist.target_hit,
  });
  const [showUpdateButton, setShowUpdateButton] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value === "Yes",
    });
    setShowUpdateButton(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
    setShowUpdateButton(false);
  };

  return (
    <div className="modal-container">
      <div className="modal-content">
        <div className="modal-header">
          <h2>{watchlist.symbol}</h2>
          <button className="close-button" onClick={onClose}>
            &times;
          </button>
        </div>
        <div className="modal-body">
          <div className="watchlist-details">
            <p>
              <strong>ID:</strong> {watchlist.id}
            </p>
            <p>
              <strong>User ID:</strong> {watchlist.user_id}
            </p>
            <p>
              <strong>Entry Date:</strong> ({formatDate(watchlist.entry_date)})
            </p>
            <p>
              <strong>Price:</strong> {watchlist.price}
            </p>
            <p>
              <strong>Target Price:</strong> {watchlist.target_price}
            </p>
            <p>
              <strong>Exp. Date:</strong> {watchlist.exp_date}
            </p>
            <p>
              <strong>Plan:</strong> {watchlist.plan}
            </p>
            <div className="form-group">
              <label>
                Target Hit:
                <select
                  name="target_hit"
                  value={formData.target_hit ? "Yes" : "No"}
                  onChange={handleChange}
                  required
                >
                  <option value="Yes">Yes</option>
                  <option value="No">No</option>
                </select>
              </label>
            </div>
            {showUpdateButton && (
              <button onClick={handleSubmit} className="update-button">
                Update
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UpdateWatchlist;
