import React, { useState, useEffect } from "react";
import { fetchWatch } from "./ApiCalls";

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedWatch, setSelectedWatch] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    dates: "",
    symbol: "",
    price: "",
    target_price: "",
    exp_date: "",
    plan: "",
  });

  // Fetch watchlist data from API
  useEffect(() => {
    const fetchWatchlist = async () => {
      try {
        const response = await fetch("http://localhost:8000/watchlist/");
        const data = await response.json();
        setWatchlist(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchWatchlist();
  }, []);

  // Function to handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Function to handle form submission for editing watchlist item
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `http://localhost:8000/watchlist/${selectedWatch.id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(selectedWatch), // Pass the selectedWatch object to update
        }
      );
      if (!response.ok) {
        throw new Error("Failed to update watchlist item");
      }
      // Assuming the response contains the updated watchlist item data
      const updatedWatchlistItem = await response.json();
      // Update the watchlist state with the updated item
      setWatchlist((prevWatchlist) =>
        prevWatchlist.map((item) =>
          item.id === updatedWatchlistItem.id ? updatedWatchlistItem : item
        )
      );
      // Close the edit modal
      setShowEditModal(false);
    } catch (err) {
      setError(err.message);
    }
  };

  // Function to handle clicking on a watchlist item for editing
  const handleListItemClick = (watchlistItem) => {
    setSelectedWatch(watchlistItem);
    setShowEditModal(true);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="watchlist">
      <div className="header">
        <p>Watchlist</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={() => setShowModal(true)}
        ></i>
      </div>
      <div className="list">
        <ul className="item">
          {watchlist.map((entry, index) => (
            <li
              key={index}
              className="card-content"
              onClick={() => handleListItemClick(entry)}
            >
              <div className="left">
                <p className="symbol">{entry.symbol}</p>
              </div>
              <div className="right">
                <p className="dates">
                  Date: {entry.dates?.split("-").slice(1).join("/")}
                </p>
                <p className="price">Price: ${entry.price}</p>
                <p className="target-price">Target: ${entry.target_price}</p>
                <p className="exp-date">
                  Expiry: {entry.exp_date?.split("-").slice(1).join("/")}
                </p>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {showEditModal && selectedWatch && (
        <div className="form-modal">
          <div className="form-modal-content">
            <div className="header">
              <span className="close" onClick={() => setShowEditModal(false)}>
                &times;
              </span>
              <div>{selectedWatch.symbol}</div>
            </div>

            <form className="edit-trade-form" onSubmit={handleEditSubmit}>
              <label htmlFor="dates">Date:</label>
              <input
                type="date"
                name="dates"
                value={selectedWatch.dates}
                onChange={(e) =>
                  setSelectedWatch({
                    ...selectedWatch,
                    dates: e.target.value,
                  })
                }
              />

              <label htmlFor="symbol">Symbol:</label>
              <input
                type="text"
                name="symbol"
                value={selectedWatch.symbol}
                onChange={(e) =>
                  setSelectedWatch({
                    ...selectedWatch,
                    symbol: e.target.value,
                  })
                }
              />

              <label htmlFor="price">Price:</label>
              <input
                type="text"
                name="price"
                value={selectedWatch.price}
                onChange={(e) =>
                  setSelectedWatch({
                    ...selectedWatch,
                    price: e.target.value,
                  })
                }
              />

              <label htmlFor="target_price">Target Price:</label>
              <input
                type="text"
                name="target_price"
                value={selectedWatch.target_price}
                onChange={(e) =>
                  setSelectedWatch({
                    ...selectedWatch,
                    target_price: e.target.value,
                  })
                }
              />

              <label htmlFor="exp_date">Exp. Date:</label>
              <input
                type="date"
                name="exp_date"
                value={selectedWatch.exp_date}
                onChange={(e) =>
                  setSelectedWatch({
                    ...selectedWatch,
                    exp_date: e.target.value,
                  })
                }
              />

              <label htmlFor="plan">Plan:</label>
              <input
                name="plan"
                value={selectedWatch.plan}
                onChange={(e) =>
                  setSelectedWatch({
                    ...selectedWatch,
                    plan: e.target.value,
                  })
                }
              ></input>

              <button type="submit">Update</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Watchlist;
