import React, { useState, useEffect } from "react";
import { fetchFinancials } from "../api/FinancialAPI";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { formatDate, formatCash } from "../func/functions";

const IncomeExpensesComparisonChart = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const financials = await fetchFinancials();
      const formattedData = financials.map((entry) => ({
        date: formatDate(entry.entry_date),
        income: parseFloat(entry.income),
        expenses: Math.abs(parseFloat(entry.expenses)), // Using absolute values for clarity
      }));
      setData(formattedData);
    };

    fetchData();
  }, []);

  return (
    <div className="income-expenses-comparison-chart">
      <h3>Income vs. Expenses</h3>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={data}>
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip formatter={(value) => formatCash(value)} />
          <Bar dataKey="income" fill="#4a90e2" />
          <Bar dataKey="expenses" fill="#f44336" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default IncomeExpensesComparisonChart;
