//components/controllers/func/functions.js

export const formatTradeDate = (dateStr) => {
  if (!dateStr || !dateStr.includes("-")) return "";
  const dateParts = dateStr.split("-");
  const month = dateParts[1];
  const day = dateParts[2];
  return `${month}/${day}`;
};
