import React, { useState } from "react";
import "./styles/watchlist.css"; // Import CSS file

const Watchlist = ({ initialStocks }) => {
  // Initialize state to manage the list of stocks
  const [stocks, setStocks] = useState(initialStocks);

  // Function to add a new stock to the watchlist
  const addStock = () => {
    const newStock = prompt("Enter the name of the stock:");
    if (newStock) {
      setStocks([...stocks, newStock]);
    }
  };

  // Function to remove a stock from the watchlist
  const removeStock = (index) => {
    const updatedStocks = [...stocks];
    updatedStocks.splice(index, 1);
    setStocks(updatedStocks);
  };

  return (
    <div id="watchlist">
      <div id="watchlist-header">Trading Watchlist</div>
      <div id="watchlist-content">
        <ul>
          {stocks.map((stock, index) => (
            <li key={index}>
              {stock}
              <button onClick={() => removeStock(index)}>Remove</button>
            </li>
          ))}
        </ul>
        <button onClick={addStock}>Add Stock</button>
      </div>
    </div>
  );
};

export default Watchlist;
