import React, { useState, useEffect } from "react";
import { formatDate, showSuccessModal, showUnsuccessModal } from "./Utility";
import "./styles/watchlist.css";
import closeIcon from "./assets/close.png";
import settingIcon from "./assets/setting.png";
import Plan from "./Plan";

const Watchlist = ({}) => {
  const [watchlist, setWatchlist] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showWatchModal, setShowWatchModal] = useState(false);
  const [editedWatchlist, setEditedWatchlist] = useState(null);
  const [newPlan, setNewPlan] = useState({});
  const [formData, setFormData] = useState({
    enter_date: "",
    symbol: "",
    current_price: "",
    target_price: "",
    expiration_date: "",
  });

  useEffect(() => {
    fetchWatchlistData();
  }, []);

  const fetchWatchlistData = async () => {
    try {
      const response = await fetch("http://localhost:8000/watchlist");
      if (!response.ok) {
        throw new Error("Failed to fetch watchlist data");
      }
      const data = await response.json();
      setWatchlist(data);
    } catch (error) {
      console.error("Error fetching watchlist data:", error.message);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleWatchSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/watchlist", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error("Failed to add watchlist item");
      }
      const data = await response.json();
      setFormData({
        enter_date: "",
        symbol: "",
        current_price: "",
        target_price: "",
        expiration_date: "",
      });
      setShowWatchModal(false);
      showSuccessModal();
      fetchWatchlistData();
    } catch (error) {
      console.error("Error adding watchlist item:", error);
      showUnsuccessModal(error.message);
    }
  };

  const handleAddNewPlan = () => {
    setShowWatchModal(true);
  };

  const handleDeleteItem = (id) => {
    const updatedWatchlist = watchlist.filter((item) => item.watch_id !== id);
    setWatchlist(updatedWatchlist);
    console.log("Updated watchlist:", updatedWatchlist);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSettingsClick = (item, e) => {
    e.stopPropagation();
    setEditedWatchlist(item);
    setShowModal(true);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedWatchlist((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `http://localhost:8000/watchlist/${editedWatchlist.watch_id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(editedWatchlist),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to update watchlist item");
      }
      handleCloseModal();
      fetch("http://localhost:8000/watchlist")
        .then((response) => response.json())
        .then((data) => setWatchlist(data))
        .catch((error) => console.error("Error fetching watchlist:", error));
    } catch (error) {
      console.error("Error updating watchlist item:", error);
      handleCloseModal();
    }
  };
  return (
    <div className="watchlist-container">
      <div className="watchlist">
        <div className="header-card">
          <p>Watchlist</p>
          <i
            className="fa-solid fa-plus"
            id="new-btn"
            onClick={handleAddNewPlan}
          ></i>
        </div>
        <div className="list-container">
          <ul className="cards">
            {watchlist.map((item) => (
              <li className="list-item" key={item.watch_id}>
                <div className="left">
                  <div>{item.watch_id}</div>
                  <div className="symbol">{item.symbol}</div>
                </div>
                <div className="right">
                  <div className="details">
                    Date: {formatDate(item.enter_date)}
                  </div>
                  <div className="details">Price: ${item.current_price}</div>
                  <div>Target: ${item.target_price}</div>
                  <div>Exp date: {formatDate(item.expiration_date)}</div>
                  <img
                    src={closeIcon}
                    alt="Close"
                    className="close-icon"
                    onClick={() => handleDeleteItem(item.watch_id)}
                  />
                  <img
                    src={settingIcon}
                    alt="Setting"
                    className="setting-icon"
                    onClick={(e) => handleSettingsClick(item, e)}
                  />
                </div>
              </li>
            ))}
          </ul>
        </div>
        {showModal && (
          <div className="modal">
            <div className="modal-content">
              <span className="close" onClick={handleCloseModal}>
                &times;
              </span>
              <div className="card">
                <h1>{editedWatchlist.symbol}</h1>
              </div>
              {editedWatchlist && (
                <div>
                  <form className="watch-form" onSubmit={handleFormSubmit}>
                    <label htmlFor="current_price">Current Price:</label>
                    <input
                      type="text"
                      name="current_price"
                      value={editedWatchlist.current_price}
                      onChange={handleInputChange}
                    />
                    <label htmlFor="target_price">Target Price:</label>
                    <input
                      type="text"
                      name="target_price"
                      value={editedWatchlist.target_price}
                      onChange={handleInputChange}
                    />
                    <label htmlFor="expiration_date">Exp. date:</label>
                    <input
                      type="date"
                      name="expiration_date"
                      value={editedWatchlist.expiration_date}
                      onChange={handleInputChange}
                      required
                    />
                    <input type="submit" value="Submit" />
                  </form>
                </div>
              )}
            </div>
          </div>
        )}
        {showWatchModal && (
          <div className="modal">
            <div className="modal-content">
              <span className="close" onClick={() => setShowWatchModal(false)}>
                &times;
              </span>
              <div className="card">
                <h1>New Watchlist</h1>
              </div>
              <form className="watch-form" onSubmit={handleWatchSubmit}>
                <label htmlFor="enter_date">Date:</label>
                <input
                  type="date"
                  name="enter_date"
                  value={formData.enter_date}
                  onChange={handleChange}
                  required
                />
                <label htmlFor="symbol">Symbol:</label>
                <input
                  type="text"
                  name="symbol"
                  value={formData.symbol}
                  onChange={handleChange}
                  required
                />
                <label htmlFor="current_price">Current Price:</label>
                <input
                  type="text"
                  name="current_price"
                  value={formData.current_price}
                  onChange={handleChange}
                  required
                />
                <label htmlFor="target_price">Target Price:</label>
                <input
                  type="text"
                  name="target_price"
                  value={formData.target_price}
                  onChange={handleChange}
                  required
                />
                <label htmlFor="expiration_date">Expiration Date:</label>
                <input
                  type="date"
                  name="expiration_date"
                  value={formData.expiration_date}
                  onChange={handleChange}
                  required
                />
                <input type="submit" value="Add" />
              </form>
            </div>
          </div>
        )}
      </div>
      <div className="new-plan-container">
        <Plan />
      </div>
    </div>
  );
};

export default Watchlist;
