import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from "recharts";

const ProfitChart = () => {
  const [chartData, setChartData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const dailyPnls = await fetchDailyPnls();
        const data = prepareChartData(dailyPnls);
        setChartData(data);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  const prepareChartData = (pnlData) => {
    // Sort PnL data by date
    pnlData.sort((a, b) => new Date(a.date) - new Date(b.date));

    // Calculate cumulative balance
    let cumulativeBalance = 0;
    return pnlData.map((pnl) => {
      cumulativeBalance += pnl.balance;
      return {
        date: new Date(pnl.date).toLocaleDateString(),
        balance: cumulativeBalance,
      };
    });
  };

  if (error) return <div>Error loading chart data</div>;

  return (
    <div className="profit-chart-container">
      <h3>Profit Progress</h3>
      <ResponsiveContainer width="100%" height={400}>
        <LineChart data={chartData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip formatter={(value) => `$${value.toFixed(2)}`} />
          <Line
            type="monotone"
            dataKey="balance"
            stroke="#4a90e2"
            strokeWidth={2}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default ProfitChart;
