import React, { useEffect, useRef, useState } from "react";
import "./styles/accountGraph.css";

const AccountGraph = () => {
  const chartRef = useRef(null); // Reference to the canvas element
  const [topProfitableTrades, setTopProfitableTrades] = useState([]); // State to store top profitable trades

  useEffect(() => {
    // Fetch top profitable trades from API
    const fetchTopProfitableTrades = () => {
      fetch("http://localhost:8000/trades/top-profitable/")
        .then((response) => response.json())
        .then((data) => setTopProfitableTrades(data))
        .catch((error) =>
          console.error("Error fetching top profitable trades:", error)
        );
    };

    fetchTopProfitableTrades();
  }, []); // Fetch data only once on component mount

  useEffect(() => {
    if (topProfitableTrades.length === 0) return; // Don't render chart if data is not available

    // Load Chart.js from the CDN dynamically
    const script = document.createElement("script");
    script.src = "https://cdn.jsdelivr.net/npm/chart.js";
    script.async = true;
    script.onload = () => {
      // Initialize Chart.js instance after the script has loaded
      const ctx = chartRef.current.getContext("2d");
      const myChart = new window.Chart(ctx, {
        type: "bar", // Change chart type to "bar"
        data: {
          labels: topProfitableTrades.map((trade) => trade.trade_date), // Labels as trade dates
          datasets: [
            {
              label: "Profit/Loss",
              data: getData(), // Call getData function to get profit data
              backgroundColor: "rgba(54, 162, 235, 0.2)", // Blue color with transparency
              borderColor: "rgba(54, 162, 235, 1)", // Solid blue border color
              borderWidth: 1, // Width of the border
            },
          ],
        },
        options: {
          scales: {
            y: {
              beginAtZero: true, // Start y-axis from zero
              title: {
                display: true,
                text: "Profit/Loss", // Y-axis title
              },
              grid,
            },
            x: {
              title: {
                display: true,
                text: "Trade Date", // X-axis title
              },
            },
          },
        },
      });

      return () => {
        myChart.destroy(); // Clean up chart on unmount
      };
    };

    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script); // Clean up script tag on unmount
    };
  }, [topProfitableTrades]); // Re-render the chart when topProfitableTrades changes

  // Function to get profit data from topProfitableTrades
  const getData = () => {
    return topProfitableTrades.map((trade) => parseFloat(trade.profit)); // Convert profit to float
  };

  return (
    <div className="account-graph">
      <canvas ref={chartRef} /> {/* Canvas for rendering the chart */}
    </div>
  );
};

export default AccountGraph;
