import React, { useState, useEffect } from "react";
import { fetchTrades } from "./ApiCalls";

const Sort = () => {
  const [trades, setTrades] = useState([]);
  const [sortedTrades, setSortedTrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [dropdownVisible, setDropdownVisible] = useState(false);

  const toggleDropdown = () => {
    setDropdownVisible(!dropdownVisible);
  };

  const closeDropdown = () => {
    setDropdownVisible(false);
  };

  // Fetch trades from the API
  useEffect(() => {
    const fetchTradesData = async () => {
      try {
        const tradesData = await fetchTrades();
        setTrades(tradesData);
        setSortedTrades(tradesData);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchTradesData();
  }, []);

  // Function to sort trades by date
  const sortByDate = (order = "asc") => {
    const sorted = [...trades].sort((a, b) => {
      const dateA = new Date(a.entryDate);
      const dateB = new Date(b.entryDate);
      return order === "asc" ? dateA - dateB : dateB - dateA;
    });
    setSortedTrades(sorted);
    closeDropdown();
  };

  // Function to sort trades by profit/loss
  const sortByProfitLoss = (order = "asc") => {
    const sorted = [...trades].sort((a, b) => {
      return order === "asc"
        ? a.profitLoss - b.profitLoss
        : b.profitLoss - a.profitLoss;
    });
    setSortedTrades(sorted);
    closeDropdown();
  };

  // Function to filter trades by option type (calls or puts)
  const filterByOptionType = (type) => {
    const filtered = trades.filter(
      (trade) => trade.optionType.toLowerCase() === type.toLowerCase()
    );
    setSortedTrades(filtered);
    closeDropdown();
  };

  // Function to filter trades by time frame
  const filterByTimeFrame = (timeFrame) => {
    const now = new Date();
    let filteredTrades = [];
    switch (timeFrame) {
      case "lastDay":
        filteredTrades = trades.filter(
          (trade) =>
            (now - new Date(trade.entryDate)) / (1000 * 60 * 60 * 24) <= 1
        );
        break;
      case "lastWeek":
        filteredTrades = trades.filter(
          (trade) =>
            (now - new Date(trade.entryDate)) / (1000 * 60 * 60 * 24) <= 7
        );
        break;
      case "lastMonth":
        filteredTrades = trades.filter(
          (trade) =>
            (now - new Date(trade.entryDate)) / (1000 * 60 * 60 * 24) <= 30
        );
        break;
      case "lastYear":
        filteredTrades = trades.filter(
          (trade) =>
            (now - new Date(trade.entryDate)) / (1000 * 60 * 60 * 24) <= 365
        );
        break;
      default:
        filteredTrades = trades;
    }
    setSortedTrades(filteredTrades);
    closeDropdown();
  };

  if (loading) {
    return <p style={{ fontSize: "10px" }}>Loading...</p>;
  }

  if (error) {
    return <p style={{ fontSize: "10px" }}>Error: {error}</p>;
  }

  return (
    <div className="sort">
      <div className="header">
        <p>Trades</p>
        <i
          className={`fa-solid fa-plus dropbtn ${
            dropdownVisible ? "rotate" : ""
          }`}
          onClick={toggleDropdown}
        ></i>
      </div>
      {dropdownVisible && (
        <div className="dropdown-menu show">
          {/* <button onClick={() => sortByDate("asc")}>Sort by Date (ASC)</button>
          <button onClick={() => sortByDate("desc")}>
            Sort by Date (DESC)
          </button> */}
          <button onClick={() => sortByProfitLoss("asc")}>Loss</button>
          <button onClick={() => sortByProfitLoss("desc")}>Profit</button>
          <button onClick={() => filterByOptionType("call")}>Calls</button>
          <button onClick={() => filterByOptionType("put")}>Puts</button>
          <button onClick={() => filterByTimeFrame("lastDay")}>1D</button>
          <button onClick={() => filterByTimeFrame("lastWeek")}>1W</button>
          <button onClick={() => filterByTimeFrame("lastMonth")}>1M</button>
          <button onClick={() => filterByTimeFrame("lastYear")}>1Y</button>
        </div>
      )}
      <div className="list">
        <ul>
          {sortedTrades.map((trade, index) => (
            <li
              key={index}
              className="card-content"
              style={{
                backgroundColor: trade.profitLoss < 0 ? "#e6b4b4" : "#dfe7de",
              }}
            >
              <p className="symbol">{trade.symbol}</p>
              <p className="option-type">{trade.optionType}</p>
              <p className="pnl">${(trade.profitLoss * 100).toFixed(2)}</p>
              <div className="right">
                <p className="dates">
                  {trade.entryDate?.split("-").slice(1).join("/")}
                </p>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Sort;
