// Account.js

import Cash from "../controllers/cash/Cash";
import React, { useState, useEffect } from "react";

const Accounts = () => {
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("access_token");
    if (token) {
      try {
        const decodedToken = JSON.parse(localStorage.getItem("user_data"));
        setUserId(decodedToken ? decodedToken.user_id : null);
      } catch (e) {
        console.error("Error decoding token", e);
      }
    }
  }, []);

  return (
    <div className="Accounts">
      <div>
        <h1>Welcome to Your Account</h1>
        <p>
          This is your user dashboard where you can manage your account settings
          and view your trading activity.
        </p>
        {userId && <Cash userId={userId} />}
        {/* Pass userId to Cash component */}
      </div>
    </div>
  );
};

export default Accounts;
