import React, { useState, useEffect } from "react";
import { performAnalysis } from "./AnalysisGet";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

const AnalysisDisplay = () => {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const results = await performAnalysis();
        setAnalysisResults(results);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  if (error) {
    console.error("Error fetching analysis data:", error);
  }

  if (!analysisResults && !error) {
    return <div>Loading...</div>;
  }

  const { averageWin = 0, averageLoss = 0 } = analysisResults || {};

  // Calculate win and loss percentages for the circle
  const total = averageWin + averageLoss;
  const winPercent = total > 0 ? (averageWin / total) * 100 : 0;
  const lossPercent = 100 - winPercent;

  return (
    <div style={{ width: "150px", height: "150px", margin: "20px auto" }}>
      <CircularProgressbar
        value={winPercent}
        text={`${winPercent.toFixed(2)}% Win`}
        styles={buildStyles({
          pathColor: "#4caf50", // Green for Avg Win
          trailColor: "#f44336", // Red for Avg Loss
          textColor: "#333",
          pathTransitionDuration: 0.5,
        })}
      />
      <div style={{ textAlign: "center", marginTop: "10px" }}>
        <div>Avg Win: ${averageWin.toFixed(2)}</div>
        <div>Avg Loss: ${averageLoss.toFixed(2)}</div>
      </div>
    </div>
  );
};

export default AnalysisDisplay;

