import React, { useEffect, useState } from "react";
import {
  FaChartLine,
  FaPercentage,
  FaMoneyBillWave,
  FaCalendarAlt,
} from "react-icons/fa";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { fetchTransactions } from "../api/TransactionsAPI";
import { fetchTrades } from "../api/TradesAPI";
import { getCash } from "../api/CashApi";
import {
  calculateInitialCash,
  calculateAvailableCash,
  calculateNetPL,
  calculateCashBalance,
  calculateROI,
} from "./CashCalc";
import { formatDate, formatCash } from "../func/functions";

const DailyPNL = () => {
  const [cashData, setCashData] = useState(null);
  const [dailyPnls, setDailyPnls] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [trades, setTrades] = useState([]); // Will hold the logged trades (closed trades)
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [
          cashResponse,
          pnlResponse,
          transactionsResponse,
          tradesResponse,
        ] = await Promise.all([
          getCash(),
          fetchDailyPnls(),
          fetchTransactions(),
          fetchTrades(), // You would add this API to fetch logged trades if available
        ]);

        setCashData(cashResponse);
        setDailyPnls(pnlResponse);
        setTransactions(transactionsResponse);
        setTrades(tradesResponse); // Set trades if you fetch them via API
      } catch (error) {
        setError("Error fetching data");
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;
  if (!cashData || !dailyPnls.length || !transactions.length)
    return <p>No data available</p>;

  // Calculate the metrics based on cash, transactions, and logged trades
  const initialCash = calculateInitialCash(cashData);
  const netPL = calculateNetPL(trades); // Summing up profit/loss of trades
  const availableCash = calculateAvailableCash(
    initialCash,
    transactions,
    trades
  );
  const cashBalance = calculateCashBalance(initialCash, netPL, transactions);
  const roi = calculateROI(initialCash, netPL);

  return (
    <div className="daily-pnl-container">
      <div className="daily-pnl-card-container">
        {/* Left section: Investment details */}
        <div className="investment-details-card">
          <div className="card-content">
            <FaMoneyBillWave className="card-icon" />
            <span className="label">Initial Cash:</span>
            <span className="value">{formatCash(initialCash)}</span>
          </div>
          <div className="card-content">
            <FaPercentage className="card-icon" />
            <span className="label">Current ROI:</span>
            <span className="value">{`${roi.toFixed(2)}%`}</span>
          </div>
          <div className="card-content">
            <FaMoneyBillWave className="card-icon" />
            <span className="label">Available Cash:</span>
            <span className="value">{formatCash(availableCash)}</span>
          </div>
          <div className="card-content">
            <FaCalendarAlt className="card-icon" />
            <span className="label">Last Updated:</span>
            <span className="value">{formatDate(new Date())}</span>
          </div>
        </div>

        {/* Right section: Balance and ROI */}
        <div className="cash-section">
          <div className="card-content">
            <FaChartLine className="card-icon" />
            <span className="label">Cash Balance:</span>
            <span className="value">{formatCash(cashBalance)}</span>
          </div>
          <div className="card-content">
            <FaPercentage className="card-icon" />
            <span className="label">ROI:</span>
            <span className="value">{`${roi.toFixed(2)}%`}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DailyPNL;

