import React, { useState, useEffect } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  PieChart,
  Pie,
  Cell,
  ResponsiveContainer,
} from "recharts";
import { performAnalysis } from "./AnalysisGet";
import { Line } from "rc-progress";

const AnalysisDisplay = () => {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const results = await performAnalysis();
        setAnalysisResults(results);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  if (error) {
    console.error("Error fetching analysis data:", error);
  }

  if (!analysisResults && !error) {
    return <div>Loading...</div>;
  }

  const {
    absoluteReturn = 0,
    sharpeRatio = 0,
    winRate = 0,
    numberOfTrades = 0,
    winningTrades = 0,
    losingTrades = 0,
    averageWin = 0,
    averageLoss = 0,
  } = analysisResults || {};

  const total = averageWin + averageLoss;
  const winPercent = total > 0 ? (averageWin / total) * 100 : 50;
  const formatCash = (value) => `$${value.toFixed(2)}`;

  const data = [
    {
      name: "Average Win",
      value: winPercent,
      color: "#4a90e2",
      amount: averageWin,
    },
    {
      name: "Average Loss",
      value: 100 - winPercent,
      color: "#f44336",
      amount: averageLoss,
    },
  ];

  const winRateData = [
    { name: "Win Rate", value: winRate, color: "#4a90e2" },
    { name: "Remaining", value: 100 - winRate, color: "#d8e3ef" },
  ];

  const renderPieChart = (chartData, tooltipFormat) => (
    <ResponsiveContainer width="100%" height={150}>
      <PieChart>
        <Pie
          data={chartData}
          innerRadius={50}
          outerRadius={70}
          startAngle={90}
          endAngle={-270}
          stroke="none"
          dataKey="value"
        >
          {chartData.map((entry, index) => (
            <Cell
              key={`cell-${index}`}
              fill={entry.color}
              style={{ transition: "filter 0.3s", cursor: "pointer" }}
              onMouseEnter={(e) => {
                e.currentTarget.style.filter = `drop-shadow(0 0 5px ${entry.color})`;
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.filter = "none";
              }}
            />
          ))}
        </Pie>
        <Tooltip cursor={false} content={tooltipFormat} />
      </PieChart>
    </ResponsiveContainer>
  );

  return (
    <div className="analysis-container">
      <div className="analysis-row">
        <div className="analysis-circle">
          <h3>Trades</h3>
          <p>{numberOfTrades}</p>
        </div>
        <div className="analysis-circle">
          <h3>Wins</h3>
          <p>{winningTrades}</p>
        </div>
        <div className="analysis-circle">
          <h3>Losses</h3>
          <p>{losingTrades}</p>
        </div>
      </div>

      <div className="analysis-circle-row">
        {/* Win Rate Chart */}
        {renderPieChart(winRateData, ({ payload }) => {
          if (payload && payload.length) {
            const { name, value } = payload[0].payload;
            return (
              <div className="tooltip-content">
                <p>{name}</p>
                <p className="amount">{`${value.toFixed(2)}%`}</p>
              </div>
            );
          }
          return null;
        })}

        {/* Average Win/Loss Chart */}
        {renderPieChart(data, ({ payload }) => {
          if (payload && payload.length) {
            const { name, amount } = payload[0].payload;
            return (
              <div className="tooltip-content">
                <p>{name}</p>
                <p className="amount">{formatCash(amount)}</p>
              </div>
            );
          }
          return null;
        })}
      </div>
    </div>
  );
};

export default AnalysisDisplay;

