import React, { useEffect, useRef, useState } from "react";
import { fetchTrades } from "./ApiCalls";

const Graph = () => {
  // Reference to the chart canvas element
  const chartRef = useRef(null);

  // State variables for loading status, trade data, and profits
  const [loading, setLoading] = useState(true);
  const [trades, setTrades] = useState([]);
  const [profits, setProfits] = useState({});

  // Fetch trade data from backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const tradeData = await fetchTrades();
        setTrades(tradeData);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching trade data:", error);
      }
    };

    fetchData();
  }, []);

  // Function to calculate profits for different time periods
  useEffect(() => {
    const calculateProfits = () => {
      if (trades.length > 0) {
        // Initialize profit data object
        const profitData = {
          "1D": 0,
          "1W": 0,
          "1M": 0,
          "3M": 0,
          YTD: 0,
          "1Y": 0,
          Max: 0,
        };

        // Calculate profits for different time periods
        trades.forEach((trade) => {
          // Calculate time difference in days
          const closeDate = new Date(trade.closeDate);
          const currentDate = new Date();
          const timeDiff = Math.abs(currentDate - closeDate);
          const daysDiff = Math.ceil(timeDiff / (1000 * 60 * 60 * 24));

          // Update profit data based on time period
          if (daysDiff <= 1) profitData["1D"] += trade.profit_loss;
          if (daysDiff <= 7) profitData["1W"] += trade.profit_loss;
          if (daysDiff <= 30) profitData["1M"] += trade.profit_loss;
          if (daysDiff <= 90) profitData["3M"] += trade.profit_loss;

          // Handle YTD and 1Y based on close date
          const currentYear = currentDate.getFullYear();
          const closeYear = closeDate.getFullYear();
          if (currentYear === closeYear) profitData.YTD += trade.profit_loss;

          // Handle 1Y based on close date
          if (currentYear - closeYear === 1)
            profitData["1Y"] += trade.profit_loss;

          // Update Max profit
          profitData.Max += trade.profit_loss;
        });

        setProfits(profitData);
      }
    };

    calculateProfits();
  }, [trades]);

  // Effect hook to render chart when trade data and profits are available
  useEffect(() => {
    if (!loading && Object.keys(profits).length > 0) {
      const ctx = chartRef.current.getContext("2d");
      const profitValues = Object.values(profits);
      const backgroundColors = profitValues.map((value) =>
        value >= 0 ? "#bed1be" : "rgb(255, 204, 204)"
      );

      const myChart = new window.Chart(ctx, {
        type: "bar",
        data: {
          labels: Object.keys(profits),
          datasets: [
            {
              label: "Profit/Loss",
              data: profitValues,
              backgroundColor: backgroundColors,
              barThickness: 40,
            },
          ],
        },
        options: {
          plugins: {
            legend: {
              display: false,
            },
          },
          scales: {
            x: {
              grid: {
                display: false,
              },
            },
            y: {
              display: true,
              title: {
                display: true,
                text: "Profit/Loss",
              },
              grid: {
                color: (context) =>
                  context.tick.value === 0
                    ? "rgba(0, 0, 0, 1)"
                    : "rgba(0, 0, 0, 0)",
                lineWidth: 0.5,
              },
              ticks: {
                callback: function (value, index, values) {
                  return value === 0 ? value : "";
                },
              },
            },
          },
        },
      });

      // Cleanup function to destroy chart instance
      return () => {
        if (myChart) {
          myChart.destroy();
        }
      };
    }
  }, [loading, profits]);

  // Render loading message or chart canvas
  return (
    <div className="graph">
      {loading ? <p>Loading...</p> : <canvas ref={chartRef} />}
    </div>
  );
};

export default Graph;
