import React, { useEffect, useState } from "react";
import { FaChartLine, FaPercentage, FaMoneyBillWave } from "react-icons/fa";
import { fetchDailyPnls } from "../api/DailyPNLApi";

const DailyPNL = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  return (
    <div className="daily-pnl-container">
      <div className="daily-pnl-card-container">
        {loading && <p>Loading...</p>}
        {error && <p>{error}</p>}
        {!loading && !error && dailyPNLData.length === 0 && (
          <p>No Daily PNL data available</p>
        )}
        {dailyPNLData.map((pnl) => (
          <div key={pnl.id} className="daily-pnl-card">
            <div className="card-content">
              <FaChartLine className="card-icon" />
              <span className="label">Net P/L:</span>
              <span className="value">${pnl.balance.toFixed(2)}</span>
            </div>
            <div className="card-content">
              <FaMoneyBillWave className="card-icon" />
              <span className="label">Balance:</span>
              <span className="value">${pnl.balance.toFixed(2)}</span>
            </div>
            <div className="card-content">
              <FaPercentage className="card-icon" />
              <span className="label">RoI:</span>
              <span className="value">{pnl.roi.toFixed(2)}%</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DailyPNL;
