import React, { useState, useRef } from "react";
import "./styles/newTrade.css";
import { capitalizeOptionSymbol, formatOptionType } from "./Utility";

const NewTrade = ({ onTradeSubmit }) => {
  const [formData, setFormData] = useState({
    open_date: "",
    option_symbol: "",
    option_type: "",
    strike_price: "",
    expiration_date: "",
    entry_price: "",
    exit_price: "",
    contracts: "",
    close_date: "",
    stock_price: "",
    implied_volatility: "",
    rationale: "",
    feedback: "",
    outcome: "",
  });

  const symbolInputRef = useRef(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    let updatedValue = value === "" ? null : value;

    if (name === "option_symbol") {
      updatedValue = capitalizeOptionSymbol(updatedValue);
    } else if (name === "option_type") {
      updatedValue = formatOptionType(updatedValue);
    }

    setFormData((prevData) => ({
      ...prevData,
      [name]: updatedValue,
    }));
  };

  //submit the form
  const handleSubmit = (e) => {
    e.preventDefault();
    // put null values if not available
    const updatedFormData = {
      ...formData,
      close_date: formData.close_date || null,
      exit_price: formData.exit_price || null,
    };
    onTradeSubmit(updatedFormData);
    setFormData({
      open_date: "",
      option_symbol: "",
      option_type: "",
      strike_price: "",
      expiration_date: "",
      entry_price: "",
      exit_price: "",
      contracts: "",
      close_date: "",
      stock_price: "",
      implied_volatility: "",
      rationale: "",
      feedback: "",
      outcome: "",
    });
  };

  const handlePlusIconClick = () => {
    symbolInputRef.current.focus();
  };

  return (
    <div className="new-trade">
      <div className="card">
        <p>New Trade</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={handlePlusIconClick}
        ></i>
      </div>
      <form className="new-trade-form" onSubmit={handleSubmit}>
        <ul>
          <li>
            <label htmlFor="close_date">Open Date:</label>
            <input
              type="date"
              name="open_date"
              ref={symbolInputRef}
              value={formData.open_date}
              onChange={handleChange}
            />
          </li>
          <li>
            <label htmlFor="option_symbol">Option Symbol:</label>
            <input
              type="text"
              name="option_symbol"
              // ref={symbolInputRef}
              value={formData.option_symbol}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="option_type">Option Type:</label>
            <input
              type="text"
              name="option_type"
              value={formData.option_type}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="strike_price">Strike Price:</label>
            <input
              type="text"
              name="strike_price"
              value={formData.strike_price}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="expiration_date">Exp. date:</label>
            <input
              type="date"
              name="expiration_date"
              value={formData.expiration_date}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="entry_price">Entry Price:</label>
            <input
              type="text"
              name="entry_price"
              value={formData.entry_price}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="exit_price">Exit Price:</label>
            <input
              type="text"
              name="exit_price"
              value={formData.exit_price}
              onChange={handleChange}
            />
          </li>
          <li>
            <label htmlFor="contracts">Contracts:</label>
            <input
              type="text"
              name="contracts"
              value={formData.contracts}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="close_date">Close Date:</label>
            <input
              type="date"
              name="close_date"
              value={formData.close_date}
              onChange={handleChange}
            />
          </li>
          <li>
            <label htmlFor="stock_price">Stock Price:</label>
            <input
              type="text"
              name="stock_price"
              value={formData.stock_price}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="implied_volatility">Implied Volatility:</label>
            <input
              type="text"
              name="implied_volatility"
              value={formData.implied_volatility}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="rationale">Rationale:</label>
            <input
              type="text"
              name="rationale"
              value={formData.rationale}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="feedback">Feedback:</label>
            <input
              type="text"
              name="feedback"
              value={formData.feedback}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="outcome">Outcome:</label>
            <input
              type="text"
              name="outcome"
              value={formData.outcome}
              onChange={handleChange}
              required
            />
          </li>
        </ul>
        <input type="submit" value="Submit" />
      </form>
    </div>
  );
};

export default NewTrade;
