import React, { useState, useEffect } from "react";
import { getCash } from "../api/CashApi";
import NewCash from "./NewCash";

const Cash = ({ userId }) => {
  const [cashData, setCashData] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const fetchCashData = async () => {
      try {
        const data = await getCash(userId);
        setCashData(data);
      } catch (error) {
        console.error("Error fetching cash data:", error);
      }
    };

    if (userId) {
      fetchCashData();
    }
  }, [userId]);

  const handleNewCash = (newCash) => {
    setCashData((prevData) => [...prevData, newCash]);
  };

  return (
    <div className="cash-container">
      <div className="header-card">
        <p className="title">Cash</p>
        <i
          className="btn btn-primary fa-solid fa-plus"
          id="trade-new-btn"
          onClick={handleOpenModal}
        ></i>
      </div>
      <button onClick={() => setShowModal(true)}>+ Add Cash</button>
      {cashData ? (
        <div>
          <h2>Current Cash Balance</h2>
          <ul>
            {cashData.map((cash) => (
              <li key={cash.id}>
                <strong>Type:</strong> {cash.transaction_type} |
                <strong>Amount:</strong> ${cash.amount} |<strong>Date:</strong>{" "}
                {cash.transaction_date} |<strong>Summary:</strong>{" "}
                {cash.transaction_summary}
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>Loading cash data...</p>
      )}
      {showModal && (
        <NewCash
          onClose={() => setShowModal(false)}
          onNewCash={handleNewCash}
        />
      )}
    </div>
  );
};

export default Cash;
