import React, { useState, useEffect } from "react";
import { fetchFinancials } from "../api/FinancialAPI";
import {
  LineChart,
  Line,
  XAxis,
  Tooltip,
  ResponsiveContainer,
  YAxis,
} from "recharts";
import { formatCash, formatDate } from "../func/functions";

const SavingsGrowthChart = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [chartData, setChartData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const financials = await fetchFinancials();
        const formattedData = financials.map((entry) => ({
          date: formatDate(entry.entry_date),
          LTSS: parseFloat(entry.LTSS),
          FFA: parseFloat(entry.FFA),
        }));
        setChartData(formattedData);
      } catch (error) {
        console.error("Error fetching savings data:", error);
        setError("Error fetching savings data");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="savings-growth-chart-container">
      <h3>Savings Growth Over Time</h3>

      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={chartData}>
            <XAxis dataKey="date" tickFormatter={(tick) => tick.slice(0, 7)} />
            <YAxis />
            <Tooltip
              formatter={(value) => formatCash(value)}
              labelFormatter={(label) => `Date: ${label}`}
            />
            <Line
              type="monotone"
              dataKey="LTSS"
              stroke="#4a90e2"
              strokeWidth={2}
              dot={false}
            />
            <Line
              type="monotone"
              dataKey="FFA"
              stroke="#50e3c2"
              strokeWidth={2}
              dot={false}
            />
          </LineChart>
        </ResponsiveContainer>
      )}
    </div>
  );
};

export default SavingsGrowthChart;
