useEffect(() => {
  if (topProfitableTrades.length === 0) return; // Don't render chart if data is not available

  // Extracting profits and trade dates from the fetched data
  const profits = topProfitableTrades.map((trade) => parseFloat(trade.profit));
  const tradeDates = topProfitableTrades.map(
    (trade) => new Date(trade.trade_date)
  );

  // Load Chart.js from the CDN dynamically
  const script = document.createElement("script");
  script.src = "https://cdn.jsdelivr.net/npm/chart.js";
  script.async = true;
  script.onload = () => {
    // Initialize Chart.js instance after the script has loaded
    const ctx = chartRef.current.getContext("2d");
    const myChart = new window.Chart(ctx, {
      type: "line",
      data: {
        labels: tradeDates, // Use trade dates as labels
        datasets: [
          {
            label: "Profit",
            data: profits, // Use profits as data points
            borderColor: "green", // Green color for line
            backgroundColor: "rgba(0, 0, 0, 0)", // Transparent background color
            borderWidth: 2, // Width of the line
          },
        ],
      },
      options: {
        plugins: {
          legend: {
            display: true,
            position: "top",
          },
        },
        scales: {
          x: {
            type: "time", // Use time scale for x-axis
            time: {
              unit: "day", // Display unit as day
            },
            title: {
              display: true,
              text: "Trade Date",
            },
          },
          y: {
            title: {
              display: true,
              text: "Profit",
            },
          },
        },
      },
    });

    return () => {
      myChart.destroy(); // Clean up chart on unmount
    };
  };

  document.body.appendChild(script);

  return () => {
    document.body.removeChild(script); // Clean up script tag on unmount
  };
}, [topProfitableTrades]); // Re-render the chart when topProfitableTrades changes
