import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from "recharts";
import { formatCash } from "../func/functions";

const ProfitChart = () => {
  const [chartData, setChartData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const dailyPnls = await fetchDailyPnls();
        const data = prepareChartData(dailyPnls);
        setChartData(data);
      } catch (err) {
        setError("Error loading data");
        console.error("Fetch error:", err);
      }
    };

    fetchData();
  }, []);

  const prepareChartData = (pnlData) => {
    // Sort PnL data by date for accurate cumulative calculations
    pnlData.sort((a, b) => new Date(a.entry_date) - new Date(b.entry_date));

    // Calculate cumulative balance for each day
    let cumulativeBalance = 0;
    return pnlData.map((pnl) => {
      cumulativeBalance += parseFloat(pnl.balance);
      return {
        date: new Date(pnl.entry_date).toLocaleDateString(), // Format the date
        balance: cumulativeBalance,
        openCash: parseFloat(pnl.open_cash),
        closeCash: parseFloat(pnl.close_cash),
        roi: pnl.roi,
      };
    });
  };

  if (error) return <div>{error}</div>;

  return (
    <div className="profit-chart-container">
      <h3>Profit Progress</h3>
      <ResponsiveContainer width="100%" height={400}>
        <LineChart data={chartData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis
            tickFormatter={(value) => formatCash(value)}
            label={{ value: "Balance", angle: -90, position: "insideLeft" }}
          />
          <Tooltip
            formatter={(value, name) =>
              name === "balance" ? formatCash(value) : `${value}%`
            }
            labelFormatter={(label) => `Date: ${label}`}
          />
          <Line
            type="monotone"
            dataKey="balance"
            stroke="#4a90e2"
            strokeWidth={2}
            dot={false}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default ProfitChart;
