import React from "react";
import "./styles/account.css";
import AccountGraph from "./AccountGraph";

const Account = ({ trades }) => {
  // Initial balance
  const initialBalance = 86.0;

  // Calculate total profits
  const totalProfits =
    trades.reduce((total, trade) => total + (trade.profit_loss || 0), 0) * 100; // Use 0 if profit_loss is null

  // Calculate total amount in the account
  const totalAmount = initialBalance + totalProfits;

  return (
    <div className="account">
      <div className="card">
        <p>Account</p>
        <p className="cash">${totalAmount.toFixed(2)}</p>{" "}
        {/* Display with 2 decimal places */}
      </div>
      <AccountGraph />
    </div>
  );
};

export default Account;
