import React, { useState, useEffect } from "react";

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedWatch, setSelectedWatch] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    symbol: "",
    price: "",
    target_price: "",
    exp_date: "",
    plan: "",
  });

  // Fetch watchlist data from API
  useEffect(() => {
    fetch("http://localhost:8000/watchlist/")
      .then((response) => response.json())
      .then((data) => {
        setWatchlist(data.watchlist_entries);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  // Function to handle form input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Function to handle form submission for adding watchlist item
  const handleSubmit = (e) => {
    console.log(formData);
    e.preventDefault();
    fetch("http://localhost:8000/watchlist/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to add watchlist item");
        }
        return response.json();
      })
      .then((data) => {
        // Update the watchlist state with the new item
        setWatchlist([...watchlist, data]);
        // Clear the form data
        setFormData({
          symbol: "",
          price: "",
          target_price: "",
          exp_date: "",
          plan: "",
        });
        // Close the modal
        setShowModal(false);
      })
      .catch((error) => {
        console.error("Error adding watchlist item:", error);
      });
  };

  // Function to handle clicking on a watchlist item for editing
  const handleListItemClick = (watchlistItem) => {
    setSelectedWatch(watchlistItem);
    setShowEditModal(true);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="watchlist">
      <div className="header">
        <p>Watchlist</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={() => setShowModal(true)}
        ></i>
      </div>
      <div className="list">
        <ul className="item">
          {watchlist.map((entry, index) => (
            <li
              key={index}
              className="card-content"
              onClick={() => handleListItemClick(entry)}
            >
              <div className="left">
                <p className="symbol">{entry[2]}</p>
              </div>
              <div className="right">
                <p className="price">Price: ${entry[3]}</p>
                <p className="target-price">Target: ${entry[4]}</p>
                <p className="exp-date">
                  Expiry: {entry[5]?.split("-").slice(1).join("/")}
                </p>
              </div>
            </li>
          ))}
        </ul>
      </div>
      {showModal && (
        <div className="form-modal">
          <div className="form-modal-content">
            <div className="header">
              <p>Watchlist</p>
              <span className="close" onClick={() => setShowModal(false)}>
                &times;
              </span>
            </div>
            <form className="new-trade-form" onSubmit={handleSubmit}>
              <label htmlFor="symbol">Symbol:</label>
              <input
                type="text"
                name="symbol"
                value={formData.symbol}
                onChange={handleChange}
              />

              <label htmlFor="price">Price:</label>
              <input
                type="text"
                name="price"
                value={formData.price}
                onChange={handleChange}
              />

              <label htmlFor="target_price">Target Price:</label>
              <input
                type="text"
                name="target_price"
                value={formData.target_price}
                onChange={handleChange}
              />

              <label htmlFor="exp_date">Exp. Date:</label>
              <input
                type="date"
                name="exp_date"
                value={formData.exp_date}
                onChange={handleChange}
              />

              <label htmlFor="plan">Plan:</label>
              <textarea
                name="plan"
                value={formData.plan}
                onChange={handleChange}
              ></textarea>

              <button type="submit">Add</button>
            </form>
          </div>
        </div>
      )}
      {showEditModal && selectedWatch && (
        <div className="form-modal">{/* Edit form modal content here */}</div>
      )}
    </div>
  );
};

export default Watchlist;
