import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "./components/styles/styles.css";
import Home from "./components/pages/Home";
import Trades from "./components/pages/Trades";
import Analysis from "./components/pages/Analysis";
import Accounts from "./components/pages/Accounts";
import Landing from "./components/pages/Landing";
import Networth from "./components/pages/Networth";
import Navigation from "./components/nav/Navigation";
import { ProtectedRoute } from "./components/controllers/func/functions";

const App = () => {
  const [isAuthLoaded, setAuthLoaded] = useState(false);

  // Check if user is authenticated to conditionally render routes
  useEffect(() => {
    const token = localStorage.getItem("access_token");
    setAuthLoaded(!!token); // Set based on token presence
    console.log("Auth Loaded:", isAuthLoaded); // Check state load
  }, []);

  if (!isAuthLoaded) return <div>Loading...</div>;

  return (
    <Router>
      <div className="app">
        <Navigation />
        <main>
          <Routes>
            <Route path="/start" element={<Landing />} />
            <Route
              path="/"
              element={
                <ProtectedRoute>
                  <Home />
                </ProtectedRoute>
              }
            />
            <Route
              path="/trades"
              element={
                <ProtectedRoute>
                  <Trades />
                </ProtectedRoute>
              }
            />
            <Route
              path="/analysis"
              element={
                <ProtectedRoute>
                  <Analysis />
                </ProtectedRoute>
              }
            />
            <Route
              path="/accounts"
              element={
                <ProtectedRoute>
                  <Accounts />
                </ProtectedRoute>
              }
            />
            <Route
              path="/networth"
              element={
                <ProtectedRoute>
                  <Networth />
                </ProtectedRoute>
              }
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
};

export default App;

