import React from "react";
import "./styles/account.css";
import AccountGraph from "./AccountGraph";

const Account = ({ trades }) => {
  // Calculate total profits only for closed trades
  const closedTrades = trades.filter((trade) => trade.status === "closed");
  const totalProfits =
    closedTrades.reduce((total, trade) => total + (trade.profit_loss || 0), 0) /
    100; // Divide by 100 to convert cents to dollars

  return (
    <div className="account">
      <div className="card">
        <p>Account</p>
        <p className="cash">${totalProfits.toFixed(2)}</p>{" "}
        {/* Display with 2 decimal places */}
      </div>
      <AccountGraph />
    </div>
  );
};

export default Account;
