import React, { useState, useEffect } from "react";
import "./styles/watchlist.css"; // Import CSS file
import { formatDate } from "./Utility"; // Import utility functions
import closeIcon from "./assets/close.png"; // Import cancel icon image

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);

  useEffect(() => {
    const storedWatchlist = JSON.parse(localStorage.getItem("watchlist"));
    if (storedWatchlist) {
      setWatchlist(storedWatchlist);
    } else {
      fetch("http://localhost:8000/watchlist") // Replace with your backend API URL
        .then((response) => response.json())
        .then((data) => {
          setWatchlist(data);
          localStorage.setItem("watchlist", JSON.stringify(data)); // Store watchlist in local storage
        })
        .catch((error) => console.error("Error fetching watchlist:", error));
    }
  }, []);

  const handleDeleteItem = (id) => {
    // Remove the item from the watchlist state
    const updatedWatchlist = watchlist.filter((item) => item.id !== id);
    setWatchlist(updatedWatchlist);
    localStorage.setItem("watchlist", JSON.stringify(updatedWatchlist)); // Update local storage
  };

  return (
    <div className="watchlist">
      <div className="header-card">Watchlist</div>
      <div className="list-container">
        <ul className="cards">
          {watchlist.map((item) => (
            <li
              key={item.id}
              className={
                item.target_price < item.current_price
                  ? "watch-item red"
                  : "watch-item green"
              }
            >
              <div className="watch-content">
                <p className="symbol">{item.symbol}</p>
                <p>${item.current_price}</p>
                <p>${item.target_price}</p>
                <p>{formatDate(item.expiration_date)}</p>
                {/* Close icon */}
                <img
                  src={closeIcon}
                  alt="Cancel"
                  className="cancel-icon"
                  onClick={() => handleDeleteItem(item.id)}
                />
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Watchlist;
