import React, { useState, useEffect } from "react";
import { formatDate } from "./Utility";
import "./styles/watchlist.css";
import closeIcon from "./assets/close.png";
import settingIcon from "./assets/setting.png";
import NewWatch from "./NewWatch";
import Plan from "./Plan";

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]); // eslint-disable-next-line
  const [selectedItem, setSelectedItem] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [editedWatchlist, setEditedWatchlist] = useState(null);
  const [editedPlan, setEditedPlan] = useState(null);
  const [newPlan, setNewPlan] = useState({});

  useEffect(() => {
    fetch("http://localhost:8000/watchlist")
      .then((response) => response.json())
      .then((data) => setWatchlist(data))
      .catch((error) => console.error("Error fetching watchlist:", error));
  }, []);

  const handleDeleteItem = (id) => {
    const updatedWatchlist = watchlist.filter((item) => item.watch_id !== id);
    setWatchlist(updatedWatchlist);
    console.log("Updated watchlist:", updatedWatchlist);
  };

  const handleCloseModal = () => {
    setSelectedItem(null);
    setShowModal(false);
  };

  const handleClosePlanModal = () => {
    setEditedPlan(null);
    setShowPlanModal(false);
  };

  const handleListItemClick = (item) => {
    setEditedPlan(item);
    setShowPlanModal(true);
  };

  const handleSettingsClick = (item, e) => {
    e.stopPropagation();
    setSelectedItem(item);
    setEditedWatchlist(item);
    setShowPlanModal(false);
    setShowModal(true);
  };

  // const handleInputChange = (e) => {
  //   const { name, value } = e.target;
  //   setEditedWatchlist((prevState) => ({
  //     ...prevState,
  //     [name]: value,
  //   }));
  // };
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPlan((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `http://localhost:8000/watchlist/${editedWatchlist.watch_id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(editedWatchlist),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to update watchlist item");
      }
      handleCloseModal();
      fetch("http://localhost:8000/watchlist")
        .then((response) => response.json())
        .then((data) => setWatchlist(data))
        .catch((error) => console.error("Error fetching watchlist:", error));
    } catch (error) {
      console.error("Error updating watchlist item:", error);
      handleCloseModal();
    }
  };

  const handleAddItem = (newItem) => {
    setWatchlist([...watchlist, newItem]);
  };
  const handleSubmitPlan = async (e) => {
    e.preventDefault();
    try {
      // Log the payload
      console.log("Plan payload:", JSON.stringify(editedPlan));

      // Construct the request URL
      const url = "http://localhost:8000/strategies/";
      console.log("Request URL:", url);

      const response = await fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newPlan),
      });
      if (!response.ok) {
        throw new Error("Failed to submit plan");
      }
      // Handle success
      console.log("Plan submitted successfully");
      // resetForm();
      handleClosePlanModal();
    } catch (error) {
      console.error("Error submitting plan:", error);
      // Handle error
    }
  };

  // const handleSubmitPlan = async (e) => {
  //   e.preventDefault();
  //   try {
  //     const response = await fetch("http://localhost:8000/strategies/", {
  //       method: "POST",
  //       headers: {
  //         "Content-Type": "application/json",
  //       },
  //       body: JSON.stringify(editedPlan),
  //     });
  //     if (!response.ok) {
  //       throw new Error("Failed to submit plan");
  //     }
  //     // Handle success
  //     console.log("Plan submitted successfully");
  //     // resetForm();
  //     handleClosePlanModal();
  //   } catch (error) {
  //     console.error("Error submitting plan:", error);
  //     // Handle error
  //   }
  // };

  return (
    <div className="watchlist-container">
      <div className="new-watch-container">
        <NewWatch onAdd={handleAddItem} watchlist={watchlist} />
      </div>
      <div className="watchlist">
        <div className="header-card">
          <p>Watchlist</p>
        </div>
        <div className="list-container">
          <ul className="cards">
            {watchlist.map((item) => (
              <li
                className="list-item"
                key={item.watch_id}
                onClick={() => handleListItemClick(item)}
              >
                <div className="left">
                  <div>{item.watch_id}</div>
                  <div className="symbol">{item.symbol}</div>
                </div>
                <div className="right">
                  <div className="details">
                    Date: {formatDate(item.enter_date)}
                  </div>
                  <div className="details">Price: ${item.current_price}</div>
                  <div>Target: ${item.target_price}</div>
                  <div>Exp date: {formatDate(item.expiration_date)}</div>
                  <img
                    src={closeIcon}
                    alt="Close"
                    className="close-icon"
                    onClick={() => handleDeleteItem(item.watch_id)}
                  />
                  <img
                    src={settingIcon}
                    alt="Setting"
                    className="setting-icon"
                    onClick={(e) => handleSettingsClick(item, e)}
                  />
                </div>
              </li>
            ))}
          </ul>
        </div>
        {showModal && (
          <div className="modal">
            <div className="modal-content">
              <span className="close" onClick={handleCloseModal}>
                &times;
              </span>
              <div className="card">
                <h1>{editedWatchlist.symbol}</h1>
              </div>
              {editedWatchlist && (
                <div>
                  <form className="watch-form" onSubmit={handleFormSubmit}>
                    <label htmlFor="current_price">Current Price:</label>
                    <input
                      type="text"
                      name="current_price"
                      value={editedWatchlist.current_price}
                      onChange={handleInputChange}
                    />
                    <label htmlFor="target_price">Target Price:</label>
                    <input
                      type="text"
                      name="target_price"
                      value={editedWatchlist.target_price}
                      onChange={handleInputChange}
                    />
                    <label htmlFor="expiration_date">Exp. date:</label>
                    <input
                      type="date"
                      name="expiration_date"
                      value={editedWatchlist.expiration_date}
                      onChange={handleInputChange}
                      required
                    />
                    <input type="submit" value="Submit" />
                  </form>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
      <div className="new-plan-container">
        <Plan />
      </div>
    </div>
  );
};

export default Watchlist;
