import React, { useState, useEffect } from "react";
import Trade from "./Trade";

const TradeForm = () => {
  const [showModal, setShowModal] = useState(false);

  const openModal = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div className="trade-form">
      <div className="header">
        <p>New Trade</p>
        <i className="fa-solid fa-plus" id="new-btn" onClick={openModal}></i>
      </div>
      {showModal && <Trade closeModal={closeModal} />}
    </div>
  );
};

export default TradeForm;
