import React, { useState, useEffect } from "react";
import { useSpring, animated } from "@react-spring/web";
import { performAnalysis } from "./AnalysisGet";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

const AnalysisDisplay = () => {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const results = await performAnalysis();
        setAnalysisResults(results);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  if (error) {
    console.error("Error fetching analysis data:", error);
  }

  if (!analysisResults && !error) {
    return <div>Loading...</div>;
  }

  const { averageWin = 0, averageLoss = 0 } = analysisResults || {};

  const total = averageWin + averageLoss;
  const winPercent = total > 0 ? (averageWin / total) * 100 : 50;

  // Set up animation for winPercent using react-spring
  const { animatedWinPercent } = useSpring({
    from: { animatedWinPercent: 0 },
    to: { animatedWinPercent: winPercent },
    config: { duration: 800 },
  });

  return (
    <div className="analysis-display-container">
      <div style={{ width: "100px", height: "100px", margin: "0 auto" }}>
        <animated.div style={{ width: "100%", height: "100%" }}>
          <CircularProgressbar
            value={animatedWinPercent}
            text={`${winPercent.toFixed(2)}%`}
            styles={buildStyles({
              pathColor: "#4a90e2",
              trailColor: "#f44336",
              textColor: "#4a90e2",
              textSize: "11px",
            })}
          />
        </animated.div>
      </div>
      <div className="analysis-metrics-summary">
        <div className="analysis-average-win">
          Avg Win: <span className="amount">${averageWin.toFixed(2)}</span>
        </div>
        <div className="analysis-average-loss">
          Avg Loss: <span className="amount">${averageLoss.toFixed(2)}</span>
        </div>
      </div>
    </div>
  );
};

export default AnalysisDisplay;

