import React, { useEffect, useState } from "react";
import {
  FaMoneyBillWave,
  FaChartLine,
  FaCashRegister,
  FaPercentage,
  FaChartPie,
} from "react-icons/fa";
import { getCash } from "../api/CashApi";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { fetchTransactions } from "../api/TransactionsAPI";
import {
  calculateInitialCash,
  calculateAvailableCash,
  calculateNetPL,
  calculateCashBalance,
  calculateROI,
} from "../cash/c";
import { formatCash } from "../func/functions";

const Cash = () => {
  const [cashData, setCashData] = useState(null);
  const [dailyPnls, setDailyPnls] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [cashResponse, pnlResponse, transactionsResponse] =
          await Promise.all([getCash(), fetchDailyPnls(), fetchTransactions()]);

        setCashData(cashResponse);
        setDailyPnls(pnlResponse);
        setTransactions(transactionsResponse);
      } catch (error) {
        setError("No Cash data");
        console.error("No Cash data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  // Use 'NA' as fallback if no data is available
  const initialCash = cashData
    ? formatCash(calculateInitialCash(cashData))
    : "NA";
  const availableCash =
    cashData && transactions.length > 0
      ? formatCash(
          calculateAvailableCash(calculateInitialCash(cashData), transactions)
        )
      : "NA";
  const netPL =
    dailyPnls.length > 0 ? formatCash(calculateNetPL(dailyPnls)) : "NA";
  const cashBalance =
    cashData && transactions.length > 0
      ? formatCash(
          calculateCashBalance(
            calculateInitialCash(cashData),
            calculateNetPL(dailyPnls),
            transactions
          )
        )
      : "NA";
  const roi =
    cashData && netPL !== "NA"
      ? `${calculateROI(
          calculateInitialCash(cashData),
          calculateNetPL(dailyPnls)
        ).toFixed(2)}%`
      : "NA";

  return (
    <div className="cash-container">
      <div className="cash-card-container">
        {/* Initial Cash Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaCashRegister className="card-icon" />
            <span className="label">Initial Cash:</span>
            <span className="value">{initialCash}</span>
          </div>
        </div>

        {/* Available Cash Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaChartPie className="card-icon" />
            <span className="label">Available Cash:</span>
            <span className="value">{availableCash}</span>
          </div>
        </div>

        {/* Net P/L Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaChartLine className="card-icon" />
            <span className="label">Net P/L:</span>
            <span className="value">{netPL}</span>
          </div>
        </div>

        {/* Cash Balance Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaMoneyBillWave className="card-icon" />
            <span className="label">Cash Balance:</span>
            <span className="value">{cashBalance}</span>
          </div>
        </div>

        {/* RoI Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaPercentage className="card-icon" />
            <span className="label">RoI:</span>
            <span className="value">{roi}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cash;

