// components/Summary.js
import React, { useEffect, useState } from "react";
import { fetchSummary } from "./ApiCalls";
import { successModal, unsuccessModal } from "./Utility";
const Summary = () => {
  const [summary, setSummary] = useState([]);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedSummary, setSelectedSummary] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    dates: "",
    category: "",
    summary: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (showEditModal) {
      setSelectedSummary({ ...selectedSummary, [name]: value });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const fetchSummaryData = async () => {
    try {
      const summaryData = await fetchSummary();
      if (Array.isArray(summaryData)) {
        setSummary(summaryData);
        successModal();
      } else {
        throw new Error("Fetched data is not an array");
      }
    } catch (error) {
      setError(error.message);
      unsuccessModal(error.message);
    }
  };
  useEffect(() => {
    fetchSummaryData();
  }, []);

  const handleItemClick = (summary) => {
    setSelectedSummary(summary);
    setShowEditModal(true);
  };

  const formatDate = (dateStr) => {
    if (!dateStr) return "";
    const [year, month, day] = dateStr.split("-");
    return `${year}-${month}-${day}`;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formattedData = {
        ...formData,
        dates: formatDate(formData.dates),
      };
      const response = await fetch("http://localhost:8000/summary/", {
        method: "PSOT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formattedData),
      });
      if (!response.ok) {
        {
          throw new Error("Failed to add Summary");
        }
      }
      successModal();
      setShowModal(false);
      setFormData({
        dates: "",
        category: "",
        summary: "",
      });
      fetchSummaryData();
    } catch (error) {
      console.log("Error adding Summary: ", error);
    }
    const handleEditSubmit = async (e) => {
      e.preventDefault();
      try {
        const formattedSummary = {
          id: selectedSummary.id,
          dates: formatDate(selectedSummary.dates),
          category: selectedSummary.category,
          summary: selectedSummary.summary,
        };
        const response = await fetch(
          `http://localhost:8000/summary/${selectedSummary.id}`,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(formattedSummary),
          }
        );
        if (!response.ok) {
          throw new Error("Failed to update Summary");
        }
        successModal();
        setShowEditModal(false);
        fetchSummaryData();
      } catch (error) {
        console.log("Error updating Summary: ", error);
      }
    };
  };
  return (
    <div className="summary">
      <h2>Summary</h2>
      {/* Add summary implementation here */}
      <p>Summary of trades and outcome.</p>
    </div>
  );
};

export default Summary;
