import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { formatCash, formatDate } from "../func/functions";

const Bars = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("daily");

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("No Daily PNL data");
        console.error("No Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchDailyPNLData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;
  if (!dailyPNLData) return <p>No PNL data available</p>;

  const formatMonthYear = (dateStr) => {
    if (!dateStr || typeof dateStr !== "string" || !dateStr.includes("-"))
      return "";
    const [year, month] = dateStr.split("-");
    return `${month}/${year}`;
  };

  // Header date based on selected view
  const today = new Date();
  const dailyDate = formatDate(today);
  const startOfWeek = new Date(today);
  startOfWeek.setDate(today.getDate() - today.getDay());
  const weeklyDate = `${formatDate(startOfWeek)} - ${formatDate(today)}`;
  const monthlyDate = `${today.getMonth() + 1}/${today.getFullYear()}`;
  const headerDate =
    view === "daily" ? dailyDate : view === "weekly" ? weeklyDate : monthlyDate;

  const getStartOfWeek = (date) => {
    const startOfWeek = new Date(date);
    startOfWeek.setDate(date.getDate() - date.getDay());
    startOfWeek.setHours(0, 0, 0, 0);
    return startOfWeek;
  };

  const getCurrentWeek = (data) => {
    const weekData = [];
    const startOfWeek = getStartOfWeek(new Date());
    const endOfWeek = new Date(startOfWeek);
    endOfWeek.setDate(startOfWeek.getDate() + 6);

    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      if (entryDate >= startOfWeek && entryDate <= endOfWeek) {
        weekData.push(entry);
      }
    });

    return weekData.sort(
      (a, b) => new Date(a.entry_date) - new Date(b.entry_date)
    );
  };

  const getCurrentMonth = (data) => {
    const weeks = [];
    let week = [];
    const startOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);

    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      if (
        entryDate >= startOfMonth &&
        entryDate.getMonth() === today.getMonth()
      ) {
        week.push(entry);
        if (week.length >= 7 || entryDate.getDay() === 6) {
          weeks.push(week);
          week = [];
        }
      }
    });

    if (week.length) weeks.push(week);
    return weeks;
  };

  const calculateBars = () => {
    let balances = [];
    let labels = [];

    if (view === "daily") {
      const currentWeek = getCurrentWeek(dailyPNLData);
      balances = currentWeek.map((day) => parseFloat(day.balance || 0));
      labels = currentWeek.map((day) =>
        day.entry_date ? formatDate(day.entry_date) : "Date"
      );
    } else if (view === "weekly") {
      const currentMonth = getCurrentMonth(dailyPNLData);
      currentMonth.forEach((week) => {
        const weekSum = week.reduce(
          (sum, day) => sum + parseFloat(day.balance || 0),
          0
        );
        balances.push(weekSum);

        const startOfWeek = week[0]?.entry_date;
        const endOfWeek = week[week.length - 1]?.entry_date;
        labels.push(
          startOfWeek && endOfWeek
            ? `${formatDate(startOfWeek)} - ${formatDate(endOfWeek)}`
            : "Week"
        );
      });
    } else if (view === "monthly") {
      const months = Array.from({ length: 12 }, () => []);
      dailyPNLData.forEach((entry) => {
        const entryDate = new Date(entry.entry_date);
        months[entryDate.getMonth()].push(entry);
      });
      months.forEach((month) => {
        if (month.length > 0) {
          const monthSum = month.reduce(
            (sum, day) => sum + parseFloat(day.balance || 0),
            0
          );
          balances.push(monthSum);
          labels.push(
            month[0]?.entry_date
              ? formatMonthYear(month[0].entry_date)
              : "Month"
          );
        }
      });
    }

    return { balances, labels };
  };

  const { balances, labels } = calculateBars();
  const maxBalance = Math.max(...balances);
  const threshold = maxBalance * 5;

  return (
    <div className="main-bars-container">
      <div className="header-card">
        <div className="tab-container">
          <button
            className={`tab-button ${view === "daily" ? "active" : ""}`}
            onClick={() => setView("daily")}
          >
            1D
          </button>
          <button
            className={`tab-button ${view === "weekly" ? "active" : ""}`}
            onClick={() => setView("weekly")}
          >
            1M
          </button>
          <button
            className={`tab-button ${view === "monthly" ? "active" : ""}`}
            onClick={() => setView("monthly")}
          >
            1Y
          </button>
        </div>
        <div className="header-date">{headerDate}</div>
      </div>

      <hr />
      <div className="bars-container">
        {balances.map((balance, index) => {
          const percentage = (Math.abs(balance) / threshold) * 65;
          const isPositive = balance >= 0;
          return (
            <div key={index} className="bar-wrapper">
              <span className="bar-label">{labels[index]}</span>
              <div
                className={`bar ${isPositive ? "positive" : "negative"}`}
                style={{
                  width: `${percentage}%`,
                }}
              >
                <span className="bar-value">{formatCash(balance)}</span>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Bars;

