import React, { useState, useEffect } from "react";
import "./styles/plan.css";

const Plan = () => {
  const [strategies, setStrategies] = useState([]);

  useEffect(() => {
    fetchStrategies();
  }, []);

  useEffect(() => {
    fetch("http://localhost:8000/watchlist")
      .then((response) => response.json())
      .then((data) => setWatchlist(data))
      .catch((error) => console.error("Error fetching watchlist:", error));
  }, []);

  const fetchStrategies = async () => {
    try {
      const response = await fetch("http://localhost:8000/strategies/");
      if (!response.ok) {
        throw new Error("Failed to fetch strategies");
      }
      const data = await response.json();
      setStrategies(data);
    } catch (error) {
      console.error("Error fetching strategies:", error.message);
    }
  };

  return (
    <div className="plan">
      <div className="header-card">
        <p>Plan</p>
      </div>
      <div className="plan-list-container">
        <ul className="cards">
          {strategies.map((strategy) => (
            <li key={strategy.strategy_id} className="list-item">
              <p>Watchlist: {strategy.watch_id}</p>
              <p>Plan: {strategy.plan}</p>
              <p>Outcome: {strategy.outcome}</p>
              <p>Position: {strategy.position_sizing_rules}</p>
              <p>by: {strategy.author}</p>
              <p>Outcome: {strategy.outcome}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Plan;
