import React, { useEffect, useState } from "react";
import {
  FaMoneyBillWave,
  FaChartLine,
  FaCashRegister,
  FaPercentage,
  FaChartPie,
} from "react-icons/fa";
import { getCash } from "../api/CashApi"; // Ensure this is the correct path

const Cash = () => {
  const [cashData, setCashData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCashData = async () => {
      try {
        const data = await getCash();
        setCashData(data);
      } catch (error) {
        setError("Error fetching cash data");
        console.error("Error fetching cash data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchCashData();
  }, []);

  return (
    <div className="cash-container">
      <div className="cash-card-container">
        {/* Initial Cash Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaCashRegister className="card-icon" />
            <span className="label">Initial Cash:</span>
            <span className="value">
              {loading
                ? "Loading..."
                : error
                ? "Fund Account"
                : `$${cashData?.initial_cash || "Fund Account"}`}
            </span>
          </div>
        </div>

        {/* Available Cash Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaChartPie className="card-icon" />
            <span className="label">Available Cash:</span>
            <span className="value">
              {loading
                ? "Loading..."
                : error
                ? "N/A"
                : `$${cashData?.available_cash || "N/A"}`}
            </span>
          </div>
        </div>

        {/* Net P/L Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaChartLine className="card-icon" />
            <span className="label">Net P/L:</span>
            <span className="value">
              {loading
                ? "Loading..."
                : error
                ? "N/A"
                : `$${cashData?.net_pnl || "N/A"}`}
            </span>
          </div>
        </div>

        {/* Cash Balance Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaMoneyBillWave className="card-icon" />
            <span className="label">Cash Balance:</span>
            <span className="value">
              {loading
                ? "Loading..."
                : error
                ? "N/A"
                : `$${cashData?.cash_balance || "N/A"}`}
            </span>
          </div>
        </div>

        {/* RoI Card */}
        <div className="cash-card">
          <div className="card-content">
            <FaPercentage className="card-icon" />
            <span className="label">RoI:</span>
            <span className="value">
              {loading
                ? "Loading..."
                : error
                ? "N/A"
                : `${cashData?.roi || "N/A"}%`}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cash;
