import React, { useState, useEffect } from "react";
import "./styles/styles.css"; // Import the stylesheet
import Account from "./Account";
import NewTrade from "./NewTrade";
import RecentTrades from "./RecentTrades";
import BiggestWinsLosses from "./BiggestWinsLosses";
import AccountGraph from "./AccountGraph";

const Home = () => {
  const [recentTrades, setRecentTrades] = useState([]);

  useEffect(() => {
    // Fetch recent trades from the backend
    fetch("http://localhost:8000/trades/")
      .then((response) => response.json())
      .then((data) => setRecentTrades(data))
      .catch((error) => console.error("Error fetching recent trades:", error));
  }, []);

  // Function to format date string to a readable format
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const options = {
      month: "short",
      day: "numeric",
      hour: "numeric",
      minute: "numeric",
    };
    return date.toLocaleDateString("en-US", options);
  };

  // Function to handle form submission of new trade
  const handleNewTradeSubmit = (formData) => {
    // POST request to backend to add new trade
    fetch("http://localhost:8000/trades/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        // Update state with the newly added trade
        setRecentTrades((prevTrades) => [data, ...prevTrades]);
      })
      .catch((error) => console.error("Error adding new trade:", error));
  };

  return (
    <div className="grid-container">
      <Account />
      <AccountGraph />
      <NewTrade onTradeSubmit={handleNewTradeSubmit} />
      <RecentTrades recentTrades={recentTrades} formatDate={formatDate} />
      <BiggestWinsLosses trades={recentTrades} />
    </div>
  );
};

export default Home;
