import React, { useEffect, useRef } from "react";
import "./styles/watchlist.css";
import { makeElementDraggable } from "./Utility";

const Watchlist = () => {
  const watchlistRef = useRef(null);

  useEffect(() => {
    if (watchlistRef.current) {
      makeElementDraggable(watchlistRef.current);
    }
  }, []);

  return (
    <div id="watchlist" ref={watchlistRef}>
      <div id="watchlist-header">Trading Watchlist</div>
      <div id="watchlist-content">
        <ul>
          <li>Stock 1</li>
          <li>Stock 2</li>
          <li>Stock 3</li>
          {/* Add more stocks as needed */}
        </ul>
      </div>
    </div>
  );
};

export default Watchlist;
