// Watchlist.js
import React, { useState, useEffect } from "react";
import { fetchWatch } from "./ApiCalls";
import { successModal, unsuccessModal } from "./Utility"; // Assuming you have modals to handle success and error states

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false); // eslint-disable-next-line
  const [selectedWatch, setSelectedWatch] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [formData, setFormData] = useState({
    dates: "",
    symbol: "",
    price: "",
    target_price: "",
    exp_date: "",
    plan: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (showEditModal) {
      setSelectedWatch({ ...selectedWatch, [name]: value });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  // Fetch watchlist data from API
  const fetchWatchData = async () => {
    try {
      const watchdata = await fetchWatch();
      setWatchlist(watchdata);
      successModal();
    } catch (error) {
      setError(error.message);
      unsuccessModal(error.message);
    }
  };
  useEffect(() => {
    fetchWatchData();
  }, []);

  if (error) return <div>Error: {error}</div>;

  return (
    <div className="watchlist">
      <h2>Watchlist</h2>
      <ul>
        {watchlist.map((entry) => (
          <li key={entry.id}>
            {entry.date} - {entry.symbol} - {entry.price} - {entry.target_price}{" "}
            - {entry.exp_date} - {entry.plan}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Watchlist;

