// Login.js

import React, { useState } from "react";
import { loginUser } from "./ApiCalls";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const credentials = { username, password };
      const response = await loginUser(credentials);
      localStorage.setItem("token", response.access_token); // Store token in localStorage
      setSuccess("Login successful! Redirecting...");
      setTimeout(() => {
        // Redirect or update your state as needed (e.g., to your dashboard)
        window.location.href = "/dashboard"; // Change to your desired route
      }, 2000); // Delay for user feedback
    } catch (err) {
      setError(err.message);
      setSuccess(""); // Clear success message
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}
      <form onSubmit={handleLogin}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
