import React, { useState, useEffect } from "react";
import { fetchRules, updateRule } from "../api/RulesAPI";
import NewRule from "./NewRule";
import UpdateRule from "./UpdateRule";
import { formatDate } from "../func/functions";

const Rules = () => {
  const [rules, setRules] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedRule, setSelectedRule] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const getRules = async () => {
      try {
        const data = await fetchRules();
        setRules(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    getRules();
  }, []);

  const handleOpenModal = (rule = null) => {
    setSelectedRule(rule);
    setIsEditing(!!rule);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setSelectedRule(null);
    setIsEditing(false);
    setShowModal(false);
  };

  const handleSaveChanges = async (updatedData) => {
    if (isEditing) {
      try {
        await updateRule(selectedRule.id, updatedData);
        setRules((prevRules) =>
          prevRules.map((r) =>
            r.id === selectedRule.id ? { ...r, ...updatedData } : r
          )
        );
      } catch (error) {
        console.error("Error updating rule:", error);
      }
    } else {
      setRules([...rules, updatedData]);
    }
    handleCloseModal();
  };

  if (loading) {
    return <div>Loading rules...</div>;
  }

  if (error) {
    return <div>Error fetching rules: {error}</div>;
  }

  return (
    <div className="rules-container">
      <div className="header-card">
        <p className="title">Rules</p>
        <div className="tooltip">
          <i
            className="btn btn-primary fa-solid fa-plus"
            id="rule-new-btn"
            onClick={() => handleOpenModal()}
          ></i>
          <span className="tooltiptext">New Rule</span>
        </div>
      </div>

      <hr />
      <div className="rules-list">
        {rules.map((rule) => (
          <div
            className="rule-item"
            key={rule.id}
            onClick={() => handleOpenModal(rule)}
          >
            <span className="rule-item-date">
              {formatDate(rule.entry_date)}
            </span>
            <span className="rule-item-text">{rule.rule}</span>
          </div>
        ))}
      </div>

      {showModal && isEditing && selectedRule && (
        <UpdateRule
          rule={selectedRule}
          onClose={handleCloseModal}
          onSave={handleSaveChanges}
        />
      )}

      {showModal && !isEditing && (
        <NewRule onClose={handleCloseModal} onSave={handleSaveChanges} />
      )}
    </div>
  );
};

export default Rules;
