// trading-app/trading-frontend/src/components/controllers/api/TradesAPI.js

const API_BASE_URL = "http://localhost:8000"; // Ensure this is the correct URL for your API

// Fetch all trades with pagination
export const fetchTrades = async () => {
  try {
    const response = await fetch(`${API_BASE_URL}/trades/1`);
    if (!response.ok) throw new Error("Error fetching trades");
    return await response.json();
  } catch (error) {
    console.error("Fetch Trades Error:", error);
    throw error;
  }
};

// UserAPI.js
export const createTrade = async (tradeData, userId) => {
  const token = localStorage.getItem("access_token");

  if (!token) {
    throw new Error("User is not authenticated");
  }

  const response = await fetch(`${API_BASE_URL}/trades`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${token}`,
    },
    body: JSON.stringify(tradeData),
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || "Error creating trade");
  }

  return await response.json();
};

// Update an existing trade
export const updateTrade = async (tradeId, tradeData) => {
  try {
    const response = await fetch(`${API_BASE_URL}/trades/${tradeId}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(tradeData),
    });
    if (!response.ok) throw new Error("Error updating trade");
    return await response.json();
  } catch (error) {
    console.error("Update Trade Error:", error);
    throw error;
  }
};

// Delete a trade
export const deleteTrade = async (tradeId) => {
  try {
    const response = await fetch(`${API_BASE_URL}/trades/${tradeId}`, {
      method: "DELETE",
    });
    if (!response.ok) throw new Error("Error deleting trade");
    return await response.json();
  } catch (error) {
    console.error("Delete Trade Error:", error);
    throw error;
  }
};

// Fetch a single trade by ID
export const fetchTradeById = async (tradeId) => {
  try {
    const response = await fetch(`${API_BASE_URL}/trades/${tradeId}`);
    if (!response.ok) throw new Error("Trade not found");
    return await response.json();
  } catch (error) {
    console.error("Fetch Trade Error:", error);
    throw error;
  }
};
