import React, { useState, useRef } from "react";
import "./styles/newTrade.css";

const NewTrade = ({ onTradeSubmit }) => {
  const [formData, setFormData] = useState({
    symbol: "",
    current_price: "",
    target_price: "",
    expiration_date: "",
  });

  const symbolInputRef = useRef(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    const updatedValue = value === "" ? null : value;
    setFormData((prevData) => ({
      ...prevData,
      [name]: updatedValue,
    }));
  };

  //submit the form
  const handleSubmit = (e) => {
    e.preventDefault();
    // put null values if not available
    const updatedFormData = {
      ...formData,
      current_price: formData.current_price || null,
      target_price: formData.target_price || null,
    };
    onTradeSubmit(updatedFormData);
    setFormData({
      symbol: "",
      current_price: "",
      target_price: "",
      expiration_date: "",
    });
  };

  const handlePlusIconClick = () => {
    symbolInputRef.current.focus();
  };

  return (
    <div className="new-trade">
      <div className="card">
        <p>New Trade</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={handlePlusIconClick}
        ></i>
      </div>
      <form className="new-trade-form" onSubmit={handleSubmit}>
        <ul>
          <li>
            <label htmlFor="symbol">Symbol:</label>
            <input
              type="text"
              name="symbol"
              ref={symbolInputRef}
              value={formData.symbol}
              onChange={handleChange}
              required
            />
          </li>
          <li>
            <label htmlFor="current_price">Current Price:</label>
            <input
              type="text"
              name="current_price"
              value={formData.current_price}
              onChange={handleChange}
            />
          </li>
          <li>
            <label htmlFor="target_price">Target Price:</label>
            <input
              type="text"
              name="target_price"
              value={formData.target_price}
              onChange={handleChange}
            />
          </li>
          <li>
            <label htmlFor="expiration_date">Exp. date:</label>
            <input
              type="date"
              name="expiration_date"
              value={formData.expiration_date}
              onChange={handleChange}
              required
            />
          </li>
        </ul>
        <input type="submit" value="Submit" />
      </form>
    </div>
  );
};

export default NewTrade;
