// Updated AnalysisDisplay.js
import React, { useState, useEffect } from "react";
import { performAnalysis } from "./AnalysisGet";

const AnalysisDisplay = () => {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const results = await performAnalysis();
        setAnalysisResults(results);
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  if (error) {
    return <div>Error fetching analysis data: {error.message}</div>;
  }

  if (!analysisResults) {
    return <div>Loading...</div>;
  }

  const {
    numberOfTrades,
    winningTrades,
    losingTrades,
    winRate,
    averageWin,
    averageLoss,
    riskRewardRatio,
    // cashBalance // Include if needed
  } = analysisResults;

  // Helper function to handle NaN and undefined values
  const formatValue = (value) => {
    return isNaN(value) || value === undefined ? "0.00" : value.toFixed(2);
  };

  return (
    <div className="analysis-container">
      <div className="analysis-card">
        <h3>Number of Trades</h3>
        <p>{numberOfTrades}</p>
      </div>
      <div className="analysis-card">
        <h3>Winning Trades</h3>
        <p>{winningTrades}</p>
      </div>
      <div className="analysis-card">
        <h3>Losing Trades</h3>
        <p>{losingTrades}</p>
      </div>
      <div className="analysis-card">
        <h3>Win Rate</h3>
        <p>{formatValue(winRate)}%</p>
      </div>
      <div className="analysis-card">
        <h3>Average Win</h3>
        <p>${formatValue(averageWin)}</p>
      </div>
      <div className="analysis-card">
        <h3>Average Loss</h3>
        <p>${formatValue(averageLoss)}</p>
      </div>
      <div className="analysis-card">
        <h3>Risk-Reward Ratio</h3>
        <p>{formatValue(riskRewardRatio)}</p>
      </div>
      {/* Add more metrics as needed */}
    </div>
  );
};

export default AnalysisDisplay;
