import React, { useState, useEffect } from "react";
import { fetchWatchlists } from "../api/WatchlistApi";
import NewWatchlist from "./NewWatchlist";

const Watchlist = () => {
  const [watchlists, setWatchlists] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Fetch watchlists on component mount
  useEffect(() => {
    const getWatchlists = async () => {
      try {
        const data = await fetchWatchlists();
        setWatchlists(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    getWatchlists();
  }, []);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  if (loading) {
    return <div>Loading watchlists...</div>;
  }

  if (error) {
    return <div>Error fetching watchlists: {error}</div>;
  }

  return (
    <div className="watchlist-container">
      <div className="header-card">
        <p className="title">Watchlists</p>
        <i
          className="btn btn-primary fa-solid fa-plus"
          id="watchlist-new-btn"
          onClick={handleOpenModal}
        ></i>
      </div>
      <hr />
      <div className="watch-container">
        <table className="watch-item">
          <thead>
            <tr>
              <th>Symbol</th>
              <th>Price</th>
              <th>Target Price</th>
              <th>Target Hit</th>
              <th>Entry Date</th>
              <th>Exp. Date</th>
              <th>Plan</th>
            </tr>
          </thead>
          <tbody>
            {watchlists.map((watchlist) => (
              <tr key={watchlist.id}>
                <td>{watchlist.symbol}</td>
                <td>${watchlist.price}</td>
                <td>${watchlist.target_price}</td>
                <td>{watchlist.target_hit ? "Yes" : "No"}</td>
                <td>{watchlist.entry_date}</td>
                <td>{watchlist.exp_date}</td>
                <td>{watchlist.plan}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showModal && <NewWatchlist onClose={handleCloseModal} />}
    </div>
  );
};

export default Watchlist;
