///////////////////// FETCH TRADES /////////////////////
export const fetchRecentTrades = (
  setRecentTrades,
  showSuccessModal,
  showUnsuccessModal
) => {
  fetch("http://localhost:8000/trades/")
    .then((response) => response.json())
    .then((data) => {
      setRecentTrades(data); // Update state with fetched data
      showSuccessModal();
    })
    .catch((error) => {
      console.error("Error fetching recent trades:", error);
      showUnsuccessModal(error.message);
    });
};

///////////////////// ADD TRADE /////////////////////
export const handleNewTradeSubmit = (
  formData,
  fetchRecentTrades,
  showSuccessModal,
  showUnsuccessModal
) => {
  fetch("http://localhost:8000/trades/", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(formData),
  })
    .then((response) => {
      if (response.ok) {
        fetchRecentTrades();
        showSuccessModal();
      } else {
        throw new Error(response.statusText);
      }
    })
    .catch((error) => {
      console.error("Error adding new trade:", error);
      showUnsuccessModal(error.message);
    });
};

///////////////////// REFRESH PAGE /////////////////////
export const handleRefresh = (
  fetchRecentTrades,
  showSuccessModal,
  showUnsuccessModal
) => {
  fetchRecentTrades()
    .then(() => showSuccessModal())
    .catch((error) => {
      console.error("Error refreshing recent trades:", error);
      showUnsuccessModal(error.message);
    });
};

///////////////////// DATE /////////////////////
export const formatDate = (dateString) => {
  const date = new Date(dateString);
  const month = (date.getMonth() + 1).toString().padStart(2, "0");
  const day = date.getDate().toString().padStart(2, "0");
  const year = date.getFullYear();
  // return `${month}/${day}/${year}`;
  return `${month}/${day}`;
};

///////////////////// MODALS /////////////////////
export const showSuccessModal = () => {
  // Create a modal
  const modal = document.createElement("div");
  modal.classList.add("modal-content");
  modal.style.backgroundColor = "lightgreen";
  modal.style.position = "fixed";
  modal.style.top = "20px";
  modal.style.right = "20px";
  modal.style.borderRadius = "2px";
  modal.style.zIndex = "9999";
  modal.style.fontSize = "12px";
  modal.style.boxShadow = "0 5px 30px 0 rgba(0, 255, 0, 0.1)";

  modal.innerHTML = `
      <div class="modal-content-success">
        // <p>Success.</p>
        <Check theme="outline" size="24" fill="#333"/>
      </div>
    `;

  // Append the modal to the body
  document.body.appendChild(modal);

  setTimeout(() => {
    modal.remove();
  }, 3000);
};

export const showUnsuccessModal = (errorMessage) => {
  const modal = document.createElement("div");
  modal.classList.add("modal-content");
  modal.style.backgroundColor = "rgb(255, 204, 204)";
  modal.style.position = "fixed";
  modal.style.top = "20px";
  modal.style.right = "20px";
  modal.style.borderRadius = "2px";
  modal.style.zIndex = "9999";
  modal.style.fontSize = "12px";
  modal.style.boxShadow = "0 5px 30px 0 rgba(255, 0, 0, 0.1)";

  modal.innerHTML = `
      <div class="modal-content-error">
        <p>Error:</p>
        <p>${errorMessage}.</p>
      </div>
    `;

  // Append the modal to the body
  document.body.appendChild(modal);

  setTimeout(() => {
    modal.remove();
  }, 3000);
};
