import React, { useState, useEffect, useMemo } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { formatCash, formatDate } from "../func/functions";

const Bars = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("daily");

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  const loadingContent = <div>Loading...</div>;
  const errorContent = <div>{error}</div>;

  if (loading) return loadingContent;
  if (error) return errorContent;

  // Helper function to format date as MM/YYYY
  const formatMonthYear = (dateStr) => {
    const dateParts = dateStr.split("-");
    return dateParts.length === 2 ? `${dateParts[1]}/${dateParts[0]}` : "";
  };

  const calculateBars = (view) => {
    let balances = [];
    let labels = [];

    const dailyLength = dailyPNLData.length;

    const resetWeeklyData = () => ({ weekSum: 0, weekStartDate: "" });
    const resetMonthlyData = () => ({ monthSum: 0, monthLabel: "" });

    if (view === "daily") {
      balances = dailyPNLData.map((day) => parseFloat(day.balance));
      labels = dailyPNLData.map((day) => formatDate(day.entry_date));
    } else if (view === "weekly") {
      const { weekSum, weekStartDate } = dailyPNLData.reduce(
        (acc, day, index) => {
          acc.weekSum += parseFloat(day.balance);
          acc.weekStartDate =
            index % 5 === 0 ? day.entry_date : acc.weekStartDate;

          if ((index + 1) % 5 === 0 || index === dailyLength - 1) {
            balances.push(acc.weekSum);
            labels.push(formatDate(acc.weekStartDate));
            acc.weekSum = 0; // Reset
          }
          return acc;
        },
        resetWeeklyData()
      );
    } else if (view === "monthly") {
      const { monthSum, monthLabel } = dailyPNLData.reduce(
        (acc, day, index) => {
          acc.monthSum += parseFloat(day.balance);
          if (!acc.monthLabel) acc.monthLabel = day.entry_date;

          if ((index + 1) % 20 === 0 || index === dailyLength - 1) {
            balances.push(acc.monthSum);
            labels.push(formatMonthYear(acc.monthLabel));
            acc.monthSum = 0; // Reset
            acc.monthLabel = ""; // Reset
          }
          return acc;
        },
        resetMonthlyData()
      );
    }

    return { balances, labels };
  };

  const { balances, labels } = useMemo(
    () => calculateBars(view),
    [dailyPNLData, view]
  );
  const maxBalanceValue = Math.max(...balances);
  const threshold = maxBalanceValue > 0 ? maxBalanceValue * 1.2 : 1; // Avoid division by zero

  return (
    <div className="main-bars-container">
      <div className="header-card">
        <div className="tab-container" role="tablist">
          {["daily", "weekly", "monthly"].map((tab) => (
            <button
              key={tab}
              className={`tab-button ${view === tab ? "active" : ""}`}
              onClick={() => setView(tab)}
              role="tab"
            >
              {tab.charAt(0).toUpperCase() + tab.slice(1)}
            </button>
          ))}
        </div>
      </div>

      <hr />
      <div className="bars-container">
        {balances.map((balance, index) => {
          const percentage = (Math.abs(balance) / threshold) * 100;
          return (
            <div
              key={index}
              className="bar-wrapper"
              aria-label={`${labels[index]} balance: ${formatCash(balance)}`}
            >
              <span className="bar-label">{labels[index]}</span>
              <div
                className={`bar ${balance >= 0 ? "positive" : "negative"}`}
                style={{ width: `${percentage}%` }}
              >
                <span className="bar-value">{formatCash(balance)}</span>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Bars;
